[[include :scp-wiki-cn:theme:last-frontier]]
[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');

div#page-content {
    color: #F8F8FF;
    font-size: 0.9375rem;
    font-family: "Noto Sans SC", sans-serif;
}

li, p {
    line-height: 175%;
}

p {
    margin-block: 1.5em;
}

span.ruby {
    height: unset;
    display: inline-flex;
    flex-direction: column-reverse;
    align-items: center;
    vertical-align: top;
    text-indent: 0;
}

span.rt {
    display: block;
    margin-top: -0.625em;
    margin-bottom: 0;
    line-height: 1.25;
}


@media (max-width: 767px) {
div#page-content {
    font-size: 0.8125rem;
}
}

div#page-content h1,
div#page-content h2,
div#page-content h3,
div#page-content h4,
div#page-content h5,
div#page-content h6 {
    font-family: "Noto Sans SC", sans-serif;
    font-weight: 700;
}

div#page-content h1 {
    color: #000;
    font-weight: 700;
    text-align: center;
    margin: 4rem 0 2rem 0;
    border-top: solid 2px #CCC;
    border-bottom: solid 2px #CCC;
    letter-spacing: 0.25rem;
}

div#page-content > p,
div#page-content > div.blockquote > p {
    text-indent: 2em;
}

div#page-content h3 {
    border-left: double 6px #CCC;
    padding-left: 0.5rem;
}
[[/module]]
[[module CSS]]



body {
    color: #ffffff;
    font-family: 'Open Sans', '黑体', sans-serif;
}

#content-wrap {
    text-shadow: 0.0625rem 0.0625rem 0.0625rem #000000;
}
#header h1 a::before {
    color: #ffffff;
    content: "魔杖人";
    font-family: 'Huiwen-Mincho';
    font-size: 3.5rem;
    text-shadow: 0rem 0rem 0.3125rem #000000;
}

#header h2::before {
    color: #ffffff;
    content: "追求切实，寻求真相";
    font-family: 'Huiwen-Mincho';
    font-size: 0.9rem;
    font-style: italic;
    font-weight: bold;
    margin-left: 2.5rem;
    position: relative;
    top: 0.625rem;
}
div#container-wrap {
    background: url(http://scpsandboxcn.wikidot.com/local--files/odeozhangzhang/xiuxing.png) top left repeat-x;
    background-size: 100%;
}

div#header {
    background-image: url('http://scptestwiki.wikidot.com/local--files/sigma:vivarium-test-page/ITTV_LOGO.png');
    background-size: 7.3rem;
    background-position: 3px 29px;
}
/* -=- LINKS -=- */

a, a.newpage {
    color: #e3647c;
}

a:visited {
    color: #d2647c;
}

/* -=- BLOCKQUOTE -=- */

div.blockquote,
blockquote {
    background: rgba(135, 0, 9, 0.11);
    border: 0.125rem dotted #865757;
}

/* -=- RATING MODULE -=- */

.page-rate-widget-box .rate-points,
.page-rate-widget-box > *,
.rate-box-with-credit-button {
    background-color: rgba(107, 22, 22, 255) !important;
}

/* -=- PAGE TITLE AND H1 -=- */

#page-title,
#action-area h1 {
    color: #ffffff;
    font-weight: bold;

}

/* -=- SEARCH BAR -=- */

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #150100;
}

#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-color: #61000d;

}

/* -=- TOP BAR -=- */

#top-bar {
    background: linear-gradient(to right, transparent 25%, transparent 50%, #6b1616 75%, transparent 100%);
}

#top-bar ul li ul {
    border: 0.0625rem solid #2477ac;
}

#top-bar ul li a {
    color: #ffffff;
    text-shadow: 0rem 0rem 0.3125rem #000000;
}

#top-bar ul li:hover a,
#top-bar ul li:hover ul li a {
    background-color: #611e2c;
}

#top-bar ul li:hover a:hover {
    background: #8c465a;
}

#top-bar .open-menu a {
    background-color: #611e2c;
    border-color: #e3647c;
    color: #ffffff;
}

/* -=- SIDEBAR -=- */

#side-bar .heading {
     color: #ffffff;
}

#side-bar div.menu-item img {
    background: url("https://scp-wiki.wdfiles.com/local--files/theme%3Alast-frontier/Diamond.png");
}

#side-bar div.side-block {
    background: #2b1d1d !important;
    border: 0.3125rem double #000000;
    box-shadow: 0 0.125rem 0.375rem #000000;
    opacity: 0.9;
}

#side-bar a,
#side-bar a:visited {
    color: #ff7a98;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: #ffffff;
}

/* -=- TABBERS -=- */

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: #8c465a;
}

.yui-navset .yui-nav a,
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #360e0b;
}

.yui-navset .yui-content {
    background-color: #110500;
    border: 0.125rem dotted #865757;
}

/* -=- USER INFO -=- */

#login-status,
#login-status a {
    color: #e3647c;
}

#account-options li a,
#account-options li a:hover {
    color: #e3647c;
}

#account-options ul {
    background-color: #150100;
    border: solid 0.0625rem #e3647c;
    opacity: 1.0;
}

/* -=- IMAGES -=- */

.scp-image-block .scp-image-caption {
    background-color: #394046;
}

/* -=- TABLES -=- */

table.wiki-content-table td {
    border: 0.0625rem dotted #865757;
}

table.wiki-content-table th {
    background-color: #394046;
}

/* -=- MISCELLANEOUS -=- */

/* Changes the top border of the tags section, which is typically colored #888, to match the theme better. */
.page-tags span {
    border-top: solid 0.0625rem #f15c7d;
}

/* Changes the colors of the layout in the History tab of the page, which constrasts heavily with the background theme. */
.pager a,
.pager a:hover,
.optionstd a,
.optionstd a:hover {
    background: #83303f;
    border: solid 0.0625rem #f15c7d;
}

/* While the page is saving and loading, there is a popup on the screen. This popup was difficult to read due to the body's properties (color being white), so this changed the background to be more thematically appropriate and legible. */
.owait,
.osuccess {
    background-color: #83303f;
    border: solid 0.0625rem #f15c7d;
}

/* -=- FOOTNOTES -=- */

.hovertip {
  background: rgba(17, 0, 5, 255) !important;
  border: solid 0.0625rem #865757 !important;

}

/* -=- MEDIA SELECTORS -=- */

@media screen and (max-width: 50rem) {

    #side-bar {
        background-color: rgba(21, 1, 0, .93);
    }

}
[[/module]]

