#skrollr-body {
    width: 100%;
    height: 7.625rem;
    position: absolute;
    top: 0;
    left: 0;
    background-image:
    linear-gradient(to bottom, rgba(255,255,255,0) 65%, rgba(0,0,0,1) 100%),
    url('http://scp-wiki.wdfiles.com/local--files/the-man-in-the-white-suit-hub/header.png');
    background-color: black;
