[[module CSS]]
#page-title {display:none;}
[[/module]]

[[module CSS]]
.lst_wrap {
    text-align: center;
    max-width: 420px;
    margin: auto;
}
.lst_wrap img {
    user-select: none;
}

.lst_wrap > .lst_info {
    align-items: center;
    display: flex;
    flex-direction: column;
}
.lst_wrap > .lst_info > .lst_user {
    width: 50%;
}
.lst_wrap > .lst_info > .lst_author {
    border: 1px solid #ddd;
    border-radius: 2em;
    display: inline-flex;
    font-size: .85em;
    padding: 0.35em 0.75em;
}


.lst_wrap > .lst_content > .lst_legend {
    color: #aaa;
    font-size: .85em;
    margin: .5em 0;
    text-align: left;
}
.lst_wrap > .lst_content > .lst_sample {
    cursor: pointer;
    display: grid;
    grid-template-columns: repeat(4, 1fr);
}
.lst_wrap > .lst_content > .lst_sample > .lst_item {
    cursor: pointer;
    transition: all .05s;
}
.lst_wrap > .lst_content > .lst_sample:has(.lst_item:hover) > .lst_item {
    opacity: .5;
}
.lst_wrap > .lst_content > .lst_sample:has(.lst_item:hover) > .lst_item:hover {
    opacity: 1;
    transform: scale(1.5, 1.5);
}
[[/module]]


