[[include :scp-wiki-cn:theme:parallel]]

[[module css]]
:root{
    --basalt-bright-element-color: 127, 127, 127;
    --basalt-dark-element-color: 63, 63, 63;
    --basalt-darker-element-color: 1, 1, 1;
    --basalt-undertone: 31, 31, 31;
    --basalt-focus-color: 31, 31, 31;
    --basalt-negative-color: 63, 63, 63;
}

h1,h2,h3,h4,h5,h6 {
  text-align: center;
  margin-top: 0.7em;
  margin-bottom: 0.7em;
}
li:not(:first-child) {margin-top: 0.75em;}
li > ul {margin-top: 0.75em;}
li > br {display: none;}

ul, ol {padding-left: 15px;}

.cent {text-align: center;}

em {
  font-style: normal;
  font-weight: bold;
  color: #1a6840;
}

.three-lines td {
  text-align: center !important;
}

#avatar-hover-container {
  display: none;
}

/* anchor */

#page-content a:link,
#page-content a:visited {
    color: inherit;
    text-decoration: currentcolor solid underline;
}
#page-content a:hover,
#page-content a:active {
    background-color: #FFF;
    color: #000;
    text-decoration: #111 solid underline;
}

/* vs bar */

span.hidden {
  display: none;
}
.display-area {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  margin-left: 1.5rem;
  margin-right: 1.5rem;
}
.flex-line {
  grid-area: 1 / 1 / span 1 / span 3;
  display: flex;
  height: 2rem;
  flex-wrap: wrap;
  counter-set: counter1 0 counter2 0 counter3 0;
}
.display-area p {
  margin: 0;
}

.item.c1 {
  background-color: #004a6b;
  border-top-left-radius: 1rem;
  border-bottom-left-radius: 1rem;
}
.item.c3 {
  background-color: #831019;
  border-top-right-radius: 1rem;
  border-bottom-right-radius: 1rem;
}

.sep.left {
  border-right: 3px solid white;
}

.sep.hint {
  position: relative;
}

.sep.hint span {
    position: absolute;
    top: -3em;
    width: 100%;
    opacity: 0;
    pointer-events: none;
    background-color: rgb(255, 255, 255);
    box-shadow: 0 3px 3px 1px rgb(170, 170, 170, 50%);
    padding: 0.75ch 0;
    text-align: center;
    transition: opacity 100ms linear 100ms;
}

.sep.hint:hover :where(span, span:hover) {
    opacity: 1;
    pointer-events: auto;
}

.counter-displayer {
  text-align: center;
  justify-self: center;
  color: white;
}

.counter-displayer img {
  filter: invert(100%);
}

.counter-displayer.c1 {grid-area: 1 / 1 / span 1 / span 1;}
.counter-displayer.c2 {grid-area: 1 / 2 / span 1 / span 1;}
.counter-displayer.c3 {grid-area: 1 / 3 / span 1 / span 1;}

.counter-displayer.c1::after {
  content: counter(counter1);
  font-size: 1.3rem;
  margin-left: 0.5rem;
  vertical-align: top;
}

.counter-displayer.c3::before {
  content: counter(counter3);
  font-size: 1.3rem;
  margin-right: 0.5rem;
  vertical-align: top;
}

/* head deco from 8000kconhub and some modifications */

#header h1 a::before {
    display: none;
}

#header h1 a span::after {
    margin-bottom: 0;
}

#container #header {
    background: none;
    position: absolute;
    box-shadow: unset;
    backdrop-filter: none;
}

#page-title {
    display: none;
}

#side-bar::after {
    background-color: black;
}

.widebox {
    --img-width: 90%;
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(100dvh);
    max-width: 100dvw !important;
    background-color: black;
    background-image: url(https://scpsandboxcn.wikidot.com/local--files/collab:inevitable-game/goif-black-pure.jpg);
    background-repeat: no-repeat;
    background-size: var(--img-width) auto;
    background-position: bottom;
    top: -1rem;
}

.widebox .bannerseparator {
    position: absolute;
    z-index: 0;
    left: calc(-50dvw + 50%);
    width: 100%;
    height: calc(100dvh);
    max-width: 100dvw !important;
    top: 100dvh;
}

.widebox div.word {
    position: relative;
    top: 35%;
}

.widebox img {
    display: block;
    margin: auto;
    width: 35%;
}

.widebox .bannerseparator img {
    width: var(--img-width);
}

.widebox .word img {
    width: min(300px, 50dvw);
}

.widebox .image-container {
    padding: 0;
}

.rate-box {
    position: relative;
    height: 40dvh;
    z-index: 7;
}

.empty {
    height: 10rem;
}

/* listpages */

span.goi-display {
  font-style: italic;
  color: #666;
}

.goi-display > a[href^="/system:page-tags/tag/_2025goif"] { display: none; }
.goi-display > a {
  text-decoration: none !important;
  font-weight: bold;
}

.goi-display:has(a[href="/system:page-tags/tag/_2025goif-a"]) {
  color: #005109;
}

.goi-display:has(a[href="/system:page-tags/tag/_2025goif-b1"]) {
  color: #004a6b;
}

.goi-display:has(a[href="/system:page-tags/tag/_2025goif-b2"]) {
  color: #831019;
}

/* specified */

.board {
  float: right;
  padding: .125rem 1.45rem;
  margin-bottom: 2.5rem;
  transform: translateX(5.5em) translateY(1em) rotate(6deg);
  color: white;
  background-color: #141e1b;
  border: 4px solid white;
  shape-outside: polygon(
    18% 0%,
    133% 15%,
    125% 110%,
    10% 95%
  );
}

.board .yui-navset.yui-navset-top .yui-content {
  border: none;
}

.grid2 {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  column-gap: 1em;
}

.rule1 {
  grid-area: 1 / 1 / span 2 / span 1;
}

.blockquote.wide {
  margin-top: 8rem;
  margin-bottom: 8rem;
  padding-top: 2rem;
  padding-bottom: 4rem;
  border-radius: 1.5em 0 1.5em 0;
  border: none;
  position: relative;
  background:
    linear-gradient(black, black) left bottom,
    linear-gradient(black, black) left bottom,
    linear-gradient(black, black) left bottom,
    linear-gradient(black, black) left bottom,
    linear-gradient(black, black) left bottom,
    linear-gradient(black, black) left bottom,
    linear-gradient(black, black) right top,
    linear-gradient(black, black) right top;
  background-color: rgb(var(--blockquote-background-color));
  background-position:
    left bottom,
    left 14px bottom 0,
    left 26px bottom 0,
    left bottom,
    left 0 bottom 14px,
    left 0 bottom 26px,
    right top,
    right 0 top 2rem;
  background-repeat: no-repeat;
  background-size:
    4px 7rem,
    2px 4rem,
    2px 2.5rem,
    85% 4px,
    65% 2px,
    55% 2px,
    4px 3.5rem,
    8rem 2px;
}

.blockquote.wide::before {
  content: "";
  position: absolute;
  right: 3.5rem;
  width: 3rem;
  height: 7.3rem;
  background-repeat: no-repeat;
  background-size: cover;
}

.show div.blockquote.wide:nth-child(20n)::before {
  background-image: url(https://scpsandboxcn.wdfiles.com/local--files/collab%3Ainevitable-game/k%201.png);
  top: -5.1rem;
}

.show div.blockquote.wide:nth-child(20n+1)::before {
  background-image: url(https://scpsandboxcn.wdfiles.com/local--files/collab%3Ainevitable-game/q%202.png);
