.intro > p:first-of-type::first-letter{
  margin-top: 0.3em;
  font-size: 2em;
  font-weight: bold;
  float: left;
  margin-right: 0.1em;
}

@media (max-width: 1024px) {
  .board {
     float: none;
     transform: rotate(6deg);
  }
  .rate-box { height: 15dvh; }
  .empty { height: 3rem; }
  .grid2 { grid-template-columns: 1fr; }
}

/* 结算 */


.colmod-link-top {
