[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');

div#page-content {
    font-size: 0.9375rem;
    font-family: "Noto Sans SC", sans-serif;
    line-height: 160%;
}

blockquote,
.blockquote {
    border-radius: 1rem;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/trap/tech_g.svg");
    background-size: 2rem auto;
    background-position: 0.5rem 0.5rem;
    background-repeat: no-repeat;
}

.blockquote.notice {
    border-radius: 0;
    background-image: none;
}

blockquote h2,
.blockquote h2 {
    text-align: center;
}

div.WZ-container {
    position: relative;
    background-color: #FEFEFE;
    box-shadow: 0 0 1rem rgba(0, 0, 0, 0.1);
    margin: 0 auto;
    max-width: 600px;
}

div.WZ-flex-1 {
    display: flex;
}

div.WZ-left-box {
    background-color: #000;
    border-radius: 0.125rem;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/f:f/HS_icon.jpg");
    background-position: center;
    background-size: cover;
    width: 2.5rem;
    min-width: 2.5rem;
    flex-basis: 2.5rem;
    height: 2.5rem;
    margin: 1rem;
}

div.WZ-right-box {
    flex-grow: 1;
}

div.WZ-right-box p {
    margin: 0.5rem 1rem 0 0;
}

div.WZ-right-box strong {
    color: #369;
    display: inline-block;
    padding-top: 0.5rem;
}

div.WZ-image {
    display: flex;
    gap: 0.5rem;
    margin-top: 0.5rem;
}

div.WZ-image br {
    display: none;
}

div.WZ-image img {
    height: 6rem;
    width: 6rem;
    object-fit: cover;
}

div.WZ-flex-2 {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

div.WZ-time {
    color: #ABABAB;
}

div.WZ-time p {
    margin: 0;
}

div.WZ-dot {
    text-align: right;
    padding: 1rem;
}

div.WZ-dot p {
    display: inline-block;
    margin: 0;
    padding: 0.3rem 0.5rem;
    background-color: #EFEFEF;
    font-size: 80%;
    color: #369;
    font-weight: 900;
}
[[/module]]

