[[include :scp-wiki:theme:prom-labs]]

[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Droid+Serif:400,700,400italic,700italic');
@import url('https://fonts.googleapis.com/css2?family=Metal+Mania&display=swap');

body{ color: rgb(12, 12, 12); }

#page-title{ display: none; }

.document{
    display: inline-block;
    border-radius: .61em;
    border: 1px solid #ccc;
    background:#f0f0f0;
    float: top; width: 96.5%;
    padding: .61em;
    box-shadow: 0 2px 4px rgba(68, 68, 68, .5);
}

.request{
    border-radius: 1.22em;
    background-color: #eee;
    box-shadow: 0 2px 4px rgba(68, 68, 68, .5);
    padding: .25em;
    margin: 0 0 .61em;
    color: #0f0f0f;
    text-align: center;
}

div.rejection {
    border: 4px solid #00b300;
    border-radius: 30px;
    font-weight: bold;
    color: #00b300;
    text-align: center;
    margin: 0.5em;
}

div.rejection-wrap {
     transform: rotate(-3.8deg);
     width: 35vw;
     margin-bottom: 1em;
     margin-left: 3em;
}

@media only screen and (max-width: 768px) {
    div.rejection-wrap {
        min-width: 60vw;
        width: 70vw;
        margin-left: -3em;
    }
}

div.appeal {
    margin-top: 5em;
}

.alagadda {
      font-family: 'Droid Serif', serif;
      background-image: linear-gradient(to bottom, rgba(5, 10, 20, .2), rgba(5, 10, 20, .5) 150px, rgba(0, 4, 31, .8) 200px, rgba(0, 2, 18, 1) 400px, rgba(0, 2, 18, 1) 400px, rgba(0, 2, 18, 1) 100%), url('https://scp-wiki.wdfiles.com/local--files/theme%3Aragnarok-theme/starfield.jpg');
      width: 80%;
      margin: auto;
      font-size: 19px;
      border-radius: 10px;
      background-color: #34343B;
      padding: 1rem;
      color: #ededed;
      text-align: center;
}

.alagadda hr {
      background-color: #ededed;
      padding: 0.5px;
}

.ambassador {
      font-family: metal mania;
      font-size: 28px;
      word-break: normal !important;
      line-height: 1.35;
}

.alagadda a {
      color: #ededed;
      text-decoration: underline;
}

.alagadda a:hover {
      text-decoration: none;
}

[[/module]]

[[module CSS]]
.blink {
    font-size: 110%;
    font-weight: bold;
    text-align: center;
    animation: blinking 0.8s ease-in-out infinite alternate;
}
@keyframes blinking {
    0% { color: transparent; }
    50%, 100% { color: #339; }
}
[[/module]]

