[[module CSS]]

@import url(https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@200;300;400;500;600;700;900&display=swap);

.content-panel.standalone {
  padding: 20px 30px;
}

.canon-title {
  font-size: 200%;
  font-weight: bold;
  margin-bottom: 10px;
}

.canon-description {
  font-size: 110%;
  font-weight: bold;
  text-align: justify;
}

.canon-snippet {
  font-style: italic;
}
.canon-snippet-subtext {
  font-size: 70%;
  margin-top: 10px;
}


.list-title a{
    color: #203A43;
    background: linear-gradient(to right, #901, #808080);
    font-family: 'Noto Serif SC';
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    word-wrap: break-all;
    font-size: 150%;
    font-weight:bold;
}   

.list-title2 a{
    color: #203A43;
    background: linear-gradient(to right, #901, #808080);
    font-family: 'Noto Serif SC';
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    word-wrap: break-all;
    font-size: 180%;
    font-weight:bold;
}   


#page-title {
    text-align: center;
    font-weight:bold;
    font-size: 220%;
    color: #202020;
    font-family: 'Noto Serif SC';
}

#side-bar div.menu-item a {
    font-weight: bold;
    color: #8A6060;
}

#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
   color: #8A6060;
}

#page-title {
    margin: 0 0 0.5em 0;
    border-bottom: 2px solid #BBBBBB;
}

h1 {
    font-family: 'Noto Serif SC';
   font-size: 200%;
}

h2,h3,h4,h5,h6 {
   font-family: 'Noto Serif SC';
}

#side-bar .side-block {
    border-radius: 0;
}
hr {
    background: #DDDDDD;
    color: #DDDDDD;
    margin-left: 0px;
    margin-right: 0px;
}

.e-blockquote {
  position:relative;
  padding-top: 0px;
  padding-bottom: 0px;
  margin-bottom: 5px;
  border:solid #707070;  
  border-width:0px 0px 0px 0px;
}

.e-blockquote2 {
  position:relative;
  padding-top: 0px;
  padding-bottom: 1px;
  margin-bottom: 0px;
  border:solid #707070;  
  border-width:0px 0px 0px 0px;
}


/*============== YUI TABS ===============*/

div#page-content div.yui-navset.yui-navset-top > ul.yui-nav {
    border: none;
    display: flex;
    flex-wrap: wrap;
}

div#page-content div.yui-navset.yui-navset-top > ul.yui-nav > li {
    margin: 0;
    padding: 0;
    height: 1.5rem;
    border: none;
    flex-grow: 1;
    max-width: 100%;
}

div#page-content div.yui-navset.yui-navset-top > ul.yui-nav > li > a {
    margin: 0;
    padding: 0rem 0.8rem;
    width: 100%;
    height: 100%;
    max-width: 100%;
    text-align: center;
    overflow: hidden;
    color: rgb(255, 255, 255);
    background-image: none;
    background-color: #B1B1B1;
    box-sizing: border-box;
    transition: ease 0.25s;
    border: none;
    font-family: 'Noto Serif SC';
}

div#page-content div.yui-navset.yui-navset-top > ul.yui-nav > li > a:focus,
div#page-content div.yui-navset.yui-navset-top > ul.yui-nav > li > a:hover {
    color: rgb(255, 255, 255);
    background-color: #707070;
    zoom: unset;
}

div#page-content div.yui-navset.yui-navset-top > ul.yui-nav > li.selected > a,
div#page-content div.tui-navset.yui-navset-top > ul.yui-nav > li.selected > a:focus,
div#page-content div.yui-navset.yui-navset-top > ul.yui-nav > li.selected > a:hover {
    color: rgb(0, 0, 0);
    background-color: #F5F5F5;
    background-image: none;
    zoom: unset;
}

div.yui-navset.yui-navset-top > ul.yui-nav > li > a > em {
    display: block;
    padding: 0;
    margin: 0;
    height: 100%;
    line-height: 1.5rem;
    border: 0px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    zoom: unset;
}

div.yui-navset.yui-navset-top > div.yui-content {
    border: 0px;
    padding: 10px 0px;
    margin: 0px 0px;
    background-color: transparent;
    zoom: unset;
}

div.yui-navset.yui-navset-top > div.yui-content > div {
    padding: 1px;
    margin: -1px;
}

.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-width: 1px 1px 5px;
    Xposition: relative;
    zoom: 1;
}

