[[module CSS]]
@import url("https://psychotronicsdivision-cn.wikidot.com/wiki:css/code/1");



div#page-content {
    border: none;
    padding: 0;
}

div.image-container {
    margin: 0;
    filter: drop-shadow(var(--gru-shadow));
}

div.main-header {
    padding: 0.5rem;
    /* box-shadow: var(--gru-shadow); */
    border: solid 0.125rem var(--gru-sub-dark);
    border-radius: 0.5rem;
    background-image: linear-gradient(45deg, transparent 0, transparent 25%, var(--gru-sub-dark) 25%, var(--gru-sub-dark) 50%, transparent 0, transparent 75%, var(--gru-sub-dark) 75%, var(--gru-sub-dark) 100%);
    background-size: 0.5rem 0.5rem;
    background-repeat: repeat;
}

div.logo {
    display: flex;
    justify-content: center;
    filter: drop-shadow(var(--gru-shadow));
    -webkit-filter: drop-shadow(var(--gru-shadow));
}

div.star {
    width: 3rem;
    height: 3rem;
    background-color: var(--gru-accent);
    mask-image: var(--gru-star);
    mask-position: center center;
    mask-size: contain;
    mask-repeat: no-repeat;
    -webkit-mask-image: var(--gru-star);
    -webkit-mask-position: center center;
    -webkit-mask-size: contain;
    -webkit-mask-repeat: no-repeat;
}

div.star.s {
    width: 2rem;
    height: 2rem;
}

div.star.l {
    width: 5rem;
    height: 5rem;
}

div.main-header h1 {
    text-align: center;
    font-size: 3rem;
    letter-spacing: 0.25rem;
    font-weight: 900;
    margin: 0;
    text-shadow: var(--gru-shadow);
}

div.main-grid-container {
    display: grid;
    margin: 0.25rem 0;
    grid-template-columns: repeat(5, 1fr);
    grid-template-rows: repeat(4, 1fr);
    grid-template-areas: "a a b b b"
        "a a c c c"
        "d d d f f"
        "e e e f f";
    gap: 0.25rem;
}

@media (max-width: 767px) {
    div.main-grid-container {
        grid-template-columns: repeat(4, 1fr);
        grid-template-rows: 1fr 1fr 1fr auto;
        grid-template-areas: "a a a a"
            "b b c c"
            "d d e e"
            "f f f f";
    }
}

div.main-grid-container div.item {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    background-color: var(--gru-sub-dark);
    border: solid 0.125rem var(--gru-accent);
    overflow: hidden;
}

div.main-grid-container div.item div.content {
    padding: 0 1rem;
}

div.main-grid-container div.item div.content-news div {
    padding: 1px 1rem;
}

div.main-grid-container div.item div.content-news div:nth-child(even) {
    background-color: var(--gru-dark);
}

div.main-grid-container div.item > h2 {
    margin: 0;
    font-size: 1rem;
    padding: 0.25rem;
    background-color: var(--gru-sub-accent);
    color: var(--gru-dark);
    text-align: center;
}

div.main-grid-container div.item > h3 {
    margin: 0;
    padding: 0;
    font-size: 0.85rem;
    height: 1.25rem;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    letter-spacing: inherit;
}

div.main-grid-container div.item > h3 * {
    margin: 0;
    padding: 0 0.25rem;
    line-height: 1.25rem;
    background-color: var(--gru-accent);
    color: var(--gru-dark);
}

div.main-grid-container div.item > h3::before {
    content: "";
    background-color: var(--gru-accent);
    width: 1.25rem;
    height: 1.25rem;
    clip-path: polygon(100% 0, 100% 100%, 0 100%);
}

div.main-grid-container div.item > h1 {
    margin: 0;
    padding: 0;
    font-size: 0.85rem;
    height: 1.25rem;
    display: flex;
    justify-content: flex-start;
    align-items: center;
}

div.main-grid-container div.item > h1 * {
    padding: 0 0.25rem;
    line-height: 1.25rem;
    background-color: var(--gru-accent);
    color: var(--gru-dark);
}

div.main-grid-container div.item > h1::after {
    content: "";
    background-color: var(--gru-accent);
    width: 1.25rem;
    height: 1.25rem;
    clip-path: polygon(0 0, 100% 0, 0 100%);
}

div.main-grid-container div.item.large {
    border-color: var(--gru-sub-accent);
    border-radius: 0.5rem;
}

div.main-grid-container div.item.item-1 {
    grid-area: a;
}

div.main-grid-container div.item.item-2 {
    grid-area: b;
}

div.main-grid-container div.item.item-3 {
    grid-area: c;
}

div.main-grid-container div.item.item-4 {
    grid-area: d;
    --gru-accent: #F66;
    --gru-shadow: 0 0 0.25rem #F66;
}

div.main-grid-container div.item.item-5 {
    grid-area: e;
}

div.main-grid-container div.item.item-6 {
    grid-area: f;
}

div.blockquote {
    margin: 0.25rem 0;
    border-radius: 0.5rem;
}
[[/module]]

