[[module css]]
#page-title {display: none;}
blockquote{-webkit-margin-start: 0px;-webkit-margin-before: 2em;-webkit-margin-after: 2em;-webkit-margin-end: 10px;border: 2px solid #008000;border-top-left-radius: 30px;border-bottom-right-radius: 30px}
.gray1 blockquote{background-color:#008000}
.gray2 blockquote{background-color:#008000}

/* HEADER */
#header {
background: url(http://scpsandboxcn.wdfiles.com/local--files/breaddddd/logo5-10.png) no-repeat left 30px !important;
background-size: 104px 104px !important;
max-width: none;
width: 100%;
max-width: none;
right: 0;
left: 0;

width: 90%;
max-width: 980px;
margin: 0 auto;
}

#header h1 a {
content: "グリーン・スパロウ財団";
}

#header h1 a::before {
content: "グリーン・スパロウ財団";
}

#header h1 a span{
display: block;
content: "";
visibility: hidden;
}

#header h2 {
margin-left: 130px;
padding: 0;
clear: left;
float: left;
font-size: 120%;
max-height: 38px;
}
#header h2 span {
display: block;
margin: 0;
padding: 20px 0;
line-height: 2px;
max-height: 2px;
font-weight: bold;
color: transparent;
text-shadow: none;
}
#header h2 span::before {
content: "スズメが喰めば、未来が永らう";
display: block;
margin: 0;
padding: 0px 0;
line-height: 0px;
max-height: 0px;
font-weight: bold;
color: #32cd32;
text-shadow: 1px 1px 1px #000;
text-shadow: 2px 2px 2px rgba(0,0,0,.8);
}

#top-bar {
width: 100%;
max-width: none;
right: 0;
left: 0;
color: black !important;
}

div#container-wrap {
background: url(http://scpsandboxcn.wikidot.com/local--files/collab:yukendai-and-shineshadowd-and-sam10617/SPCWrapper%20%282%29.jpg) top left repeat-x;
}

/* SIDEBAR */
#side-bar .side-block {
border-color: #008000;
box-shadow: 0 2px 8px rgba(0,0,51,.5);
}

#side-bar img {
width:13px;
height:13px;
box-sizing:border-box;
padding-left: 15px;
background: url(http://scpsandboxcn.wikidot.com/local--files/collab:yukendai-and-shineshadowd-and-sam10617/asdfasdf.png) left top no-repeat;
}

#side-bar .heading {
color: #008400;
border-bottom: solid 1px #228b22;
}

#side-bar div.menu-item.inactive a {
color: #008400;
}

#side-block a:visited {
color: #008400;
}

#side-bar div.menu-item a:visited {
color: #5a9d02;
}

#side-bar div.menu-item .sub-text {
color: #008400;
}

#side-bar .collapsible-block-unfolded-link {
border-bottom: solid 1px #008400;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
color: #008400;
}

.interwiki_title {
color: #008400;
}

.interwiki_entry a {
color: #008400;
}

/* LINKS */

a {
color: #94cd32;
text-decoration: none;
background: transparent;
}
a.newpage {
color: #9acd32;
text-decoration: none;
background: transparent;
}

a:visited {
color: #006400;
}

/* INFO PANE */
.rate-box-with-credit-button {
background-color: #006400 !important;
border-color: #006400 !important;
}

.page-rate-widget-box{
border: none !important;
}

.rate-box-with-credit-button .fa-info {
border-color: #006400 !important;
color: #008400 !important;
}

.rate-box-with-credit-button .fa-info:hover {
color: #006400 !important;
}

.rate-box-with-credit-button .cancel {
border: none !important;
}

.close-credits{
filter: hue-rotate(210deg);
}

.modalbox {
box-shadow: 0px 20px 20px rgb(39,130,221,0.3) !important;
}

/* RATING MODULE */
div.page-rate-widget-box .rate-points {
background-color: #006400;
border: solid 1px #006400;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
background-color: #adff2f;
border-top: solid 2px #006400;
border-bottom: solid 2px #006400;
font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
background: color: #008400;
color: #0b65c0;
padding: 0 4px;
margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
background: #006400;
color: #7cfc00;
text-decoration: none;
}
.page-rate-widget-box .cancel {
background-color: #006400;
border: solid 1px #006400;
border-left: 0;
border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
background: transparent;
text-transform: uppercase;
color: #adff2f;
}
.page-rate-widget-box .cancel a:hover {
border-radius: 0 3px 3px 0;
background: #adff2f;
color: #adff2f;
text-decoration: none;
}

/* PAGE TITLEs */
h1, #page-title {
color: #008400;
}

/* SEARCH BAR */
#search-top-box-input {
border: solid 1px #008000;
background-color: #404040;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
border: solid 1px #adff2f
background-color: #adff2f;
}
#search-top-box-form input[type=submit] {
border: solid 1px #adff2f;
background-color: #132639;
color: #0b2447;
background: linear-gradient(to bottom, #2373cd,#319ec2,#183291);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
border: solid 1px #adff2f;
color: #adff2f;
background-color: #132639;
background: linear-gradient(to bottom, #2373cd,#319ec2,#adff2f);
}

.scpnet-interwiki-frame{
filter: hue-rotate(260deg) brightness(150%);
}

TAB
.yui-navset .yui-content{
background-color: white
}

.yui-navset .yui-nav a,

.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
background-color: #3cb371 !important;
background-image: none !important;
}

.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
background-color: #006400 !important;
background-image: none !important;
color: #f0ff00 !important;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
background-color: #008000;
background-image: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
border-color: #228e22;
}
