[[module css]]
sup {
    vertical-align: top;
    position: relative;
    top: -0.5em;
}
a.collapsible-block-link {
  font-weight: bold;
}
.translation_block, .u-faq { display: none; }
}
.info-container .collapsible-block-folded a{
color: #fff;}
.info-container .collapsible-block-unfolded-link a{
color: #fff;
}
#page-title {
    text-align: center;
}
.scp-image-block {
  border: solid 1px #000000;
}
.scp-image-block .scp-image-caption {
  background-color: #00000;
  border-top: solid 1px #000000;
  color: #fff;
}
.scp-image-block .scp-image-caption {
     background-color: #000000;
     border-top: solid 1px #666;
     padding: 2px 0;
     font-size: 80%;
     font-weight: bold;
     text-align: center;
     width: 300px;
     text-color: #fff;
}
 .scp-image-block > p {
     margin: 0;
}
 .scp-image-block .scp-image-caption > p {
     margin: 0;
     padding: 0 10px;
}
#page-content > .collapsible-block {
    display: block;
    text-align: center;
    padding: 0.65em;
}
#page-content > .collapsible-block .collapsible-block-content {
    text-align: left;
}
.lie{
     font-weight: bold;
     font-family: monospace;
     font-size: 140%;
}
.list{
    background: #f4f4f4;
    padding: 0 1em;
}
.info-container .collapsible-block-link,
.info-container:hover .collapsible-block-link {
    line-height: 1.6em;
}
@media (max-width: 2000px) {
    .desktop-only {
     display: none;
     visibility: hidden;
}
#page-title::after,
.meta-title::after,
#page-title::before,
.meta-title::before {
    content: "";
    flex-grow: 1;
    height: 0.0625rem;
    background: rgb(var(--swatch-primary));
}

#page-title::before,
.meta-title::before {
    margin: auto 1.25rem auto auto;
}
[[/module]]

