[[module css]]
h1, #page-title {
    color: #6C3365; /*1级标题、页面标题颜色*/
}
#page-title {
    border-color: #930093; /*页面标题边框颜色，下面那一条*/
}
a {
    color: #6C3365; /*链接颜色*/
}
a.newpage {
    color: #6C3365; /*链接新页面颜色*/
}
a:visited {
    color: #6C3365; /*访问过的链接颜色*/
}
a:hover {
    color: #C07AB8; /*鼠标悬停链接颜色*/
}
body {
    color: #000000; /*整个页面的文字基础颜色*/
}

div#container-wrap {
    background-image: url(http://scpsandboxcn.wikidot.com/local--files/happystorm-zhang-scp/Dark%20kkp.png); /*顶栏背景图片*/
    background-repeat: repeat-x; /*顶栏背景图片平铺方式：X轴方向平铺*/
}
#search-top-box-input {
    color: #FFFFFF; /*搜索栏文字颜色*/
    background-color: #0c4d66; /*搜索栏背景颜色*/
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    color: #FFFFFF; /*搜索栏悬停文字颜色*/
    background-color: #6C3365; /*搜索栏悬停背景颜色*/
}
#search-top-box-form input[type=submit] {
    border-color: #eee; /*搜索按钮边框颜色*/
    color: #FFFFFF; /*搜索按钮文字颜色*/
    background-color: #6C3365; /*搜索按钮背景颜色*/
    background: linear-gradient(to bottom, #225c7c, #6C3365, #0b3851); /*搜索按钮渐变背景*/
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-color: #f0fcff; /*搜索按钮悬停边框颜色*/
    color: #f0fcff; /*搜索按钮悬停文字颜色*/
    background-color: #225c7c; /*搜索按钮悬停背景颜色*/
    background: linear-gradient(to bottom, #E800E8, #15486b, #E800E8); /*搜索按钮悬停渐变背景*/
}

#login-status {
    color: #FFFFFF; /*账户信息文字颜色*/
}
#login-status a {
    color: #FFFFFF; /*账户信息链接颜色*/
}
#login-status ul a {
    color: #15486b; /*账户信息下拉菜单链接颜色*/
}
#account-topbutton {
    background-color: #f0fcff; /*账户信息下拉菜单背景颜色*/
    color: #000000; /*账户信息下拉菜单文字颜色*/
}

#header h1 a { /*标题*/
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
#header h1 a::before {
    content: "HAPPYSTORM的艺作天堂";
    white-space: nowrap;
    color: #FFFFFF;
    text-shadow: 3px 3px 5px #0c4d66;
}
#header h2 span { /*副标题*/
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
    user-select: none;
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
}
#header h2 span::before {
    content: "　熬夜，修仙，登神！　";
    white-space: nowrap;
    color: #FF77FF;
    text-shadow: 1px 1px 1px #0c4d66;
}

/* TOP MENU */
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #6C3365; /*顶栏菜单打开时文字颜色*/
    background-color: transparent;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background-color: #FFFFFF; /*顶栏菜单打开时悬停背景颜色*/
}
#top-bar ul li ul a, #top-bar a:hover {
    color: ##6C3365; /*顶栏菜单内下拉菜单链接颜色*/
    background-color: #FFFFFF;
}

/* SIDE MENU */
#side-bar a {
    color: #6C3365; /*侧边栏链接颜色*/
}
#side-bar a:visited {
    color: #460046; /*侧边栏访问过链接颜色*/
}
#side-bar a:hover {
    color: #FF00FF; /*侧边栏链接悬停颜色*/
}
#side-bar .side-block { /*侧边栏方块*/
    border-color: #000000; /*边框颜色*/
    background-color: #FFBFFF; /*背景颜色*/
    box-shadow: 0 2px 6px rgba(12,77,102,.5); /*阴影*/
}
#side-bar .heading { /*侧边栏标题*/
    color: #000000; /*颜色*/
    border-color: #000000; /*边框颜色，下面那一条*/
}
#side-bar div.menu-item .sub-text {
    color: #D0D0D0; /*侧边栏副标题颜色，SCP系列后面的（X000-X999）*/
}

#side-bar .collapsible-block-folded {
    background-image: none; /*侧边栏折叠背景，一个小十字，隐藏*/
}
#side-bar .collapsible-block-unfolded-link {
    border-color: #000000; /*侧边栏折叠打开是边框颜色，下面那一条*/
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #6C3365; /*侧边栏折叠链接颜色*/
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #FF00FF; /*侧边栏折叠链接悬停颜色*/
}

/* YUI-TABS */
.yui-navset .yui-content{
    background-color: #FFFFFF; /*Tab背景颜色*/
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #6C3365; /*Tab标签背景颜色，无图模式下有用*/
    background-image: url(http://scp-sandbox-3.wikidot.com/local--files/breaddddd/colorrr.png); /*Tab标签背景图片*/
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-color: #000000; /*当前Tab标签背景颜色，无图模式下有用*/
    color: #478bad; /*FFFFFF*/
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background-color: #3C3C3C; /*Tab标签悬停背景颜色，无图模式下有用*/
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #000000; /*Tab标签与内容之间的边框颜色*/
}

/* Page Rating Module Customizations */
.page-rate-widget-box .rate-points {
  background-color: #6C3365 !important; /*评分分数背景颜色*/
  border-color: #000000; /*评分分数边框颜色*/
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #fce98b; /*评分up、down按钮背景颜色*/
  border-color: #ed8f07; /*评分up、down按钮边框颜色*/
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background-color: #AE00AE; /*评分up、down按钮文字背景颜色*/
  color: #FFFFFF; /*评分up、down按钮文字颜色*/
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background-color: #6C3365; /*评分up、down按钮悬停文字背景颜色*/
  color: #AE00AE; 评分up、down按钮悬停文字颜色*/
}
.page-rate-widget-box .cancel {
  background-color: #460046; /*评分cancel按钮背景颜色*/
  border-color: #000000; /*评分cancel按钮边框颜色*/
}
.page-rate-widget-box .cancel a {
  background-color: 460046; /*评分cancel按钮文字背景颜色*/
  color: #000000; /*评分cancel按钮文字颜色*/
}
.page-rate-widget-box .cancel a:hover {
  background-color: #460046; /*评分cancel按钮悬停文字背景颜色*/
  color: #FFFFFF; /*评分cancel按钮悬停文字颜色*/
}

.scp-image-block {
    border-color: #000000; /*插图边框颜色*/
}

/* off-canvas */

@media (max-width: 767px) {
#side-bar {
    background-color: #FFBFFF; /*移动端侧边栏背景颜色*/
}
}
