[[module css]]
.ratedown.btn,
.cancel.btn {
    display:none
}
[[/module]]

[[module CSS]]
#header h1 a::before { content: "è­¦å‘Šï¼šè¿™ä¸œè¥¿è¶…çº§META" !important; }
