[[module css]]
.ratedown.btn,
.cancel.btn {
    display:none
}
[[/module]]

[[module CSS]]
#header h1 a::before { content: "警告：这东西超级META" !important; }
#header h2 span::before { content: "哎哟我去这标志竟然会转" !important; }

/* INFO BOX */
sup{
    vertical-align: top;
    position: relative;
    top: -0.5em;
}
body{
    color: rgb(12, 12, 12);
}
.translation_block, .u-faq, #u-author_block{ display: none; }

/* MIMIC BLOCKQUOTE */
 div.quote-block{
     border: 3px double #999;
}
 div.quote-block{
     background-color: #f4f4f4;
     padding: 0 1em;
     margin: 1em 3em;
}
 @media (max-width: 479px){
     div.quote-block{ margin: 1em 0; }
} @media (min-width: 480px) and (max-width: 580px){
     div.quote-block{ margin: .5em; }
}

/* DIV BLOCKS */
.pata-notice{
    border: 1px solid rgb(12, 12, 12);
    background-color: #1e122b;
    padding: 0 1em;
    margin: 1em 0;
    text-align: center;
    color: #e6e6e6;
}
.trans-quote{
    border: 1px dashed #AAA;
    padding: 1em 2em;
    background-color: white;
    text-align: center;
    font-style: italic;
}
.report-box{
    border: 2px solid rgb(12, 12, 12);
    background-color: white;
    padding: 0 1em;
    margin: 1em 0;
}
.report-box hr{ background: rgb(12, 12, 12); }
.material-box {
    border: 1px lightgrey solid;
    background-color: white;
    box-shadow: 1px 2px 2px 0 rgba(0,0,0,0.16); padding: 1em .5em;
    margin-bottom: 1.25em 0;
}
.material-box blockquote {
    border: 1px double #999;
}

/* FORMATTING */
a.footnoteref, a.footnoteref::before { color: #e817d0; }
.footnotes-footer{ display: none; }
hr{
    display: block;
    border: 0;
    padding: 0;
    margin: 1em 0;
    height: 1px;
}
.lite-header hr{
    display: block;
    border: none;
    background-color: #d9d9d9;
    margin: auto;
    height: 2px;
    width: 45%;
}
.h2{
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
    font-size: 150%;
    font-family: "Trebuchet MS", Trebuchet, Verdana, Arial, Helvetica;
}
.meta-title{
    border-bottom: 1px solid #bbb;
    padding: 0 0 .25em;
    margin: 0 0 .6em;

    color: #901;
    font-weight: normal;
    font-size: 200%;
}
.meta-title p {
    margin: 0;
}
[[/module]]

[[module CSS]]
#page-title {
    display: none;
}
border-bottom:none;
[[/module]]

[[module CSS]]
.heritage-notice {
border: dashed 2px #b00010; 
border-radius: 10px; 
width: 400px; 
margin: 0 auto; 
padding: 0 20px; 
background: #fcfcf0 url(/local--files/heritage-collection/hatch-black10i.png); 
color: #331010; 
min-height: 116px;
}

table.heritage {
  border: 0;
  padding: 0;
  margin: 0 auto;
  width: 600px;
  border-spacing: 0;
  border-collapse: separate;
}

table.heritage td {
  vertical-align: top;
}

table.heritage td.title,
table.heritage td.label {
  border-right: solid 1px #999;
  font-size: 16pt;
  color: #999;
  width: 200px;
  text-align: right;
  padding-right: 10px;
}

table.heritage td.label {
  font-size: 20pt;
}

table.heritage td.title {
  padding-bottom: 20px;
}
table.heritage td.label {
  font-weight: bold;
}

table.heritage td.entry {
  padding: 6px 0 20px 10px;
}

table.heritage div.description {
  font-size: 14pt;
}
table.heritage div.snippet {
  font-style: italic;
  font-size: 8pt;
  color: #666;
}
table.heritage div.date {
  font-weight: normal;
  font-size: 7pt;
}
table.heritage div.snippet p {
  margin: 5px 0;
}
table.heritage div.description p,
table.heritage div.date p {
  margin: 0;
}

@media (max-width: 599px) {
.heritage-notice {
padding: 0; 
}

table.heritage {
width: 100%;
}

table.heritage td.title, table.heritage td.label {
width: 40%;
}

table.heritage td.label {
font-size: 15pt;
}
}
[[/module]]

