[[module CSS]]
/* this css added by woedenaz */

.fillpg:active {
    object-fit: contain;
}

.scp-image-caption img {
    min-width: unset;
}

/* this css cleaned by mrpines */

.rate-points::before{
content: "gamer ";
}

.document {
display: inline-block;
border-radius: 10px;
border:solid 1px #ccc;
background:#f0f0f0;
float:top; width:96.5%;
padding: 10px;
box-shadow: 0 2px 4px rgba(68, 68, 68, .5);
}

.containercontainer {
position: relative;
width: auto;
height: auto;
border: 2px solid #666;
background: #141212;
color: #3F3;
margin-top: 1em;
margin-bottom: 1em;
}

.containercontainer .container .overlay {
background: rgba(51, 255, 51, 0.9);
color: #121010;
position: absolute;
width: auto;
height: 50px;
top: 0;
right: 0;
bottom: 0;
left: 0;
margin: auto;
line-height: 50px;
display: inline;
vertical-align: middle;
text-align: center;
font-family: Courier New;
font-size: 30px;
letter-spacing: 5px;
font-weight: bolder;
z-index: 3;
animation: overlay-anim 10s linear;
animation-fill-mode: forwards;
animation-iteration-count: infinite;
}

.overlayback {
background: rgba(187, 187, 187, 0.5);
z-index: 3;
position: absolute;
top: 0;
left: 0;
bottom: 0;
right: 0;
width: auto;
height: auto;
animation: overlay-anim 10s linear;
animation-fill-mode: forwards;
animation-iteration-count: infinite;
}

.containercontainer .container {
text-align: center;
width: inherit;
height: inherit;
position: relative;
overflow: hidden;
}

.containercontainer .container::after {
content: " ";
display: block;
position: absolute;
top: 0;
left: 0;
bottom: 0;
right: 0;
background: rgba(21, 19, 19, 0.2);
opacity: 0;
z-index: 2;
animation: flicker 0.15s infinite;
}

.containercontainer .container::before {
content: " ";
display: block;
position: absolute;
top: 0;
left: 0;
bottom: 0;
right: 0;
background: linear-gradient(rgba(18, 16, 16, 0.03) 50%, rgba(0, 0, 0, 0.45) 50%), linear-gradient(90deg, rgba(255, 0, 0, 0.22), rgba(0, 255, 0, 0.11), rgba(0, 0, 255, 0.22));
z-index: 2;
background-size: 100% 2px, 3px 100%;
}

.no-unfold {
text-align: center;
}

.container h1,.container h2,.container h3,.container h5,.container h6 {
color: #3F3;
}

.container hr {
border-top: 1px dotted #3F3;
border-bottom: none;
}

.container hr {
border-top: 1px solid #3F3;
border-bottom: 1px solid #3F3;
color: transparent;
background-color: transparent;
}

.container hr:after {
display: block;
margin-top: 2px;
border-top: 1px solid #3F3;
border-bottom: 1px solid #3F3;
}

.blinkbar {
animation: blink 1.5s infinite;
letter-spacing: -0.13em;
}

/* KEYFRAMES */

@keyframes blink{
to{opacity: .0;}
}

@keyframes flicker {
0% {opacity: 0.9890; }5% {opacity: 0.5842; }
10% {opacity: 0.9865; }15% {opacity: 0.0412; }
20% {opacity: 0.6255; }25% {opacity: 0.3157; }
30% {opacity: 0.7328; }35% {opacity: 0.4654; }
40% {opacity: 0.9128; }45% {opacity: 0.2449; }
50% {opacity: 0.5485; }55% {opacity: 0.6438; }
60% {opacity: 0.1180; }65% {opacity: 0.9085; }
70% {opacity: 0.0266; }75% {opacity: 0.6795; }
80% {opacity: 0.7812; }85% {opacity: 0.0117; }
90% {opacity: 0.2239; }95% {opacity: 0.8897; }
100% {opacity: 0.2260; }
}

@keyframes overlay-anim {
0% {visibility: hidden; }
10% {visibility: hidden; }
11% {visibility: visible; }
50% {visibility: hidden; }
100% {visibility: hidden; }
}

[[/module]]

