[[module css]]

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@100;300;400;500;700;900&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+Armenian:wght@100..900&display=swap');

div#extra-div-1 {
    background: url("https://scpsandboxcn.wikidot.com/local--files/sharia-vanilla/desert-night.jpg") 50% 64% / cover no-repeat;
    filter: sepia(60%) saturate(120%) brightness(80%);
}

body {
    background-color: #66462a;
    color: #fffef9c0;
}

h1, h2, h3, h4, h5, h6 {
  color: #fffef9c0 !important;
  text-align: center;
}

#page-title {
    color: #e5b751 !important;
    mask-image: linear-gradient(190deg, #0000 0%, #0000 30%, #000 70%, #000 100%);
    -webkit-mask-image: linear-gradient(190deg, #0000 0%, #0000 30%, #000 70%, #000 100%);
    text-shadow: #4f4032 0 0 4px;
}

a:link, a:visited {
    color: inherit;
    text-decoration: currentcolor solid underline;
}

a:hover, a:active {
    background-color: #000;
    color: #fff;
    text-decoration: #eee solid underline;
}

a.footnoteref {
  color: transparent;
  font-size: 0;
  cursor: help;
  text-decoration: none;
}

a.footnoteref::after {
  color: #fffef9d0;
  font-size: 0.8rem;
  content: "这是什么?";
}

div.hovertip {
  background-color: #000c !important;
}

div#side-bar {
    background: none;
    background-color: #4f4032;
}

div#side-bar div.side-block {
    background: #b5aa9060;
}

#side-bar div.menu-item :is(a, a:link, a:visited, a:hover, a:active) {
    color: #fffef9;
}

.page-rate-widget-box {
    background-color: #fffef980;
    border-radius: 0.3rem;
    box-shadow: none;
}

/* titled blockquote from parallel */

blockquote, div.blockquote {
    background-color: #4f4032d0;
    box-shadow: none;
    margin: 2em 0;
}

div#main-content div.blockquote.title {
    position: relative;
    border: 4px solid black;
    border-left: none;
    border-right: none;
    border-radius: 0;
    word-break: break-all;
}

div#main-content div.blockquote.title::before {
    content: attr(data-title);
    position: absolute;
    color: #fffef9c0;
    background-color: black;
    font-family: 'Noto Serif SC';
    font-size: 1rem;
    text-transform: uppercase;
    letter-spacing: .01em;
    padding: 0 0.5rem;
    inset-inline-start: 0;
    inset-block-end: 100%;
    transform: translateY(calc(50% - 4px * 0.5));
}

/* specified for work */

strong {
  color: #58e4c1d0;
  font-weight: 900;
}

em {
  color: #eebcaad0;
  font-style: normal;
}

span.trans {
  color: #00757a;
  background-color: #fffef980;
  padding-inline: 0.5em;
}

span.trans > span:last-of-type { display: none; }
span.trans:hover > span:first-of-type { display: none; }
span.trans:hover > span:last-of-type { display: inline; }

div#odialog-container, div#lock-info, div#odialog-container :is(h1, h2, h3, h4, h5, h6) {
  color: black;
}

/* portable toc */

.port-toc {
  position: fixed;
  z-index: 10;
  top: 20%;
  right: 0;
}

.port-toc .collapsible-block-unfolded {
  display: grid;
  grid-template-columns: 1fr 1.5rem;
}

.port-toc .collapsible-block-unfolded-link {
  grid-area: 1 / 2 / span 1 / span 1;
}

.port-toc .collapsible-block-content {
  grid-area: 1 / 1 / span 1 / span 1;
}

.port-toc a.collapsible-block-link {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 1.5rem;
  height: 5rem;
  background-color: white;
  color: black;
  text-decoration: none;
}

.port-toc a.collapsible-block-link:hover, .port-toc a.collapsible-block-link:active {
  color: black;
}

.port-toc .blockquote {
  margin: 0;
  padding: 0.5em 2em;
}

#page-content table.wiki-content-table {
  width: 100%;
}

#page-content table.wiki-content-table th {
  background-color: #320042;
  border: none;
}

#page-content table.wiki-content-table td {
  background-color: black;
  border: none;
}

.scp-image-block .scp-image-caption {
  background-color: black;
}

