[[module CSS]]
#header {
     background-image: url(http://scp-wiki-cn.wdfiles.com/local--files/hospital-hub/logo);
}
#header h1 a {
    color: transparent;
    text-shadow: none;
}
#header h1 a::before {
    content: "Hospital";
    color: #eee;
    text-shadow: 3px 3px 5px #000;
}
#header h2 span {
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: "手復神躯";
    color: #f9ffe1;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
@media (min-width: 980px) {
    #header {
        background-position: 10px 40px;
        background-size: 100px 100px;
    }
}
[[/module]]

