[[module CSS]]
@charset "utf-8";

/* 顶端栏格式 */

div#container-wrap {
    background: url(http://scp-wiki-cn.wdfiles.com/local--files/lord167akaadamnpluto/body）top left repeat-x;
})top left repeat-x;
}

#header h1 {
    margin-left: 15%;
    padding: 0;
    float: left;
    max-height: 90px;
}

#header h2 {
    margin-left: 15%;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-size: 120%;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    content: "Hospital";
    color: #FFFFFF; 
    text-shadow: 3px 3px 5px #000000; 
}
#header h2 span::before {
    content: '수복신구 ';
    color: #f9ffe1; 
    text-shadow: 1px 1px 1px #000000; 
    text-shadow: 1px 1px 1px rgba(0,0,0,.8); 
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
}

#header {
background: url(http://scp-wiki-cn.wdfiles.com/local--files/hospital-hub/test_logo) 10px 40px no-repeat;

}

}
