[[include :scp-wiki-cn:component:toggle-sidebar]]
[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Fugaz+One&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Jersey+10&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Pixelify+Sans:wght@400..700&display=swap');
:root {
--header-title: "热辣FISH";
--sp_header-title-size:5rem;
--sp_header-height: 20rem;
}
#top-bar .open-menu a {
line-height:1.05em;
border: 0.2em solid #ae3c1a;
background-color: #ffaa37;
color: #ae3c1a;
}
#side-bar{
background-image: linear-gradient(135deg, #01fff7, #40bff8, #ffffff, #b547fc, #fa01ff);
}
.heading{
color:black;
border-bottom:solid 1px purple;
}
.menu-item img{
display:none;
}
.side-block{
background: rgba(0,0,0,0.2) !important;
}
.side-block a{
color:#ff7eff !important;
font-family:'JERSEY 10';
text-shadow:2px 0 black, -2px 0 black, 0 2px black, 0 -2px black;
font-size:24px;
}
.side-block a:hover{
text-decoration: none;
background:black;
}
#login-status{
color:#67e5d4;
font-weight:bold;
grid-column:1 / 5;
padding-right:1em;
}
#login-status a{
color:#8699e0;
}
#account-options{
background:rgba(38,48,83,0.9);
border:solid 2px black;
}
#account-options a:hover{
color:#8699e0 !important;
}
#account-options a{
color:#67e5d4 !important;
font-weight:bold;
text-align:center;
}
#search-top-box{
grid-row:2 / 3 !important;
grid-column: 1 / 5;
margin-top:-4em;
margin-right:1em;
justify-content:end;
}
#search-top-box-form input[type=submit] {
color:black;
background-image:linear-gradient(to bottom, #c3f7ee, #5d7fd1, #c3f7ee);
border:solid 1px blue;
}
#search-top-box-form input[type=submit]:hover {
color:darkblue;
background-image:linear-gradient(to bottom, #5d7fd1, #c3f7ee, #5d7fd1);
border:solid 1px blue;
}
#header{
background-image:url(https://scp-wiki.wikidot.com/local--files/hotfish/synth-min.png);
background-size:contain;
}
#header h1{
text-align:center;
padding-left:0;
grid-column:1 / 5;
}
#header h1 a{
font-family:'fugaz one';
color:#ea227c;
text-shadow:1px 1px #1e030b, 4px 4px #44165b, 5px 5px #350D01, 8px 8px #d24f23, 9px 9px #f4a628;
filter:drop-shadow(1px 1px 10px black);
animation-name:rotational;
animation-duration:0.8s;
animation-timing-function:ease-in-out;
animation-iteration-count:infinite;
animation-direction:alternate;
}
@keyframes rotational {
0% {transform:rotate(-4deg) scale(1.1) translate(-10px, 0);}
50% {transform:rotate(0deg) scale(1) translate(0, 0);}
100% {transform:rotate(4deg) scale(1.1) translate(10px, 0);}
}
#header::before, #header h2{
display:none;
}
#top-bar{
margin:0;
background:black;
}
#top-bar div[class*="top-bar"] > ul {
justify-content:center;
}
#top-bar ul li {
flex-grow:1;
}
#top-bar :is(.top-bar, .mobile-top-bar) > ul > li > a {
color:#e2395d;
}




#content-wrap{
margin:0;
max-width:100%;
width:100%;
}
#main-content{
max-width:100%;
width:100%;
background:black;
color:white;
font-family:'pixelify sans';
}
#main-content a{
color:#fb5efa;
}
#page-title{
display:none;
}

.pseudomain{
max-width:800px;
margin:auto;
padding:10px;
}
.punkrock{
border-top:solid 3px white;
border-bottom:solid 3px white;
display:flex;
flex-grow:1;
height:250px;
}
.mess{
background-image:url(https://scp-wiki.wikidot.com/local--files/hotfish/mess.png);
background-size:cover;
background-repeat: no-repeat;
background-position: bottom;
width:70%;
}
.oc{
background:linear-gradient(to bottom,#00c7ca,#02484c);
width:30%;
position:relative;
}
.oc:before{
position:absolute;
content:"";
background-image:url(https://scp-wiki.wikidot.com/local--files/hotfish/froggychair.png);
background-size:contain;
background-repeat:no-repeat;
transform:translate(-50%,-50%);
filter: drop-shadow(0px 0px 5px black);
width:300px;
height:300px;
top:-12%;
left:-6%;
animation-name:rotational2;
animation-duration:2s;
animation-timing-function:ease-in-out;
animation-iteration-count:infinite;
animation-direction:alternate;
z-index:2;
}
@keyframes rotational2{
0% {transform:rotate(-4deg);}
100% {transform:rotate(4deg);}
}
.oc:after{
position:absolute;
background:linear-gradient(to bottom,#00c7ca,#02484c);
content: '';
clip-path: polygon(0 0, 100% 100%, 100% 0);
height:250px;
right:100%;
top:0;
width:50px;
z-index:1;
}
.hardset{
font-size:200%;
color:transparent;
animation: 2s ease-in-out infinite alternate pulse;
}
@keyframes pulse {
0% {text-shadow:rgba(255,255,255,0.9) -.06em -.06em, rgba(179,255,251,0.9) 0 0}
33% {text-shadow:rgba(234, 255, 254,0.9) -.15em -.15em, rgba(104, 124, 150,0.9) 0 0}
66% {text-shadow:rgba(214, 255, 253,0.9) -.06em -.06em,rgba(141, 103, 175,0.9) 0 0}
100% {text-shadow:rgba(179, 255, 251,0.9) -.15em -.15em,rgba(202, 69, 215,0.9) 0 0}
}
.running{
clip-path: polygon(0 10%, 100% 0%, 100% 90%, 0 100%);
background-image: linear-gradient(to bottom, #ff013e, #9d095b);
color:#fb61df;
text-shadow:2px 2px black;
font-size:220%;
padding-left:2em;
padding-right:2em;
}
.two{
background-image: linear-gradient(to bottom, #e911ed, #15ccd5);
color:#494b4e;
}
.three{
background-image: linear-gradient(to bottom, #19d739, #75e167);
color:red;
}
.four{
background-image: linear-gradient(to bottom, #f8f693, #ff8701);
color:purple;
}
,five{
background-image: linear-gradient(to bottom, #2e24a9, #da4899);
color:white;
}
.running a{
font-family:'fugaz one';
}
.running a:hover{
background:#019333;
}

blockquote{
background:#008b8b;
}
#footer{
margin-top:0;
}
