[[module css]]
:root {
    --sidebar-width: 19em;
    --header-height: 162px;
}

#container {
    position: absolute;
    width: 100%;
    display: grid;
    grid-template-rows: 162px 1fr;
    grid-auto-rows: auto;
    background: url(http://shitake-crude-production.wdfiles.com/local--files/component%3Atheme/body_bg.png) top left repeat-x;
}

#header {
    padding: 0;
    grid-row: 1;
}

#content-wrap {
    margin: 0 auto;
    padding: 0;
    width: 100%;
    grid-row: 2;
    display: grid;
    grid-template-columns: 19em 1fr;
}

#side-bar {
    display: none;
    margin: 0;
    padding: 0;
    width: 100%;
    grid-column: 1/2;
}

#main-content {
    display: grid;
    grid-column: 1 / -1;
    grid-template-columns: 19em 1fr;
    grid-auto-rows: max-content;
    grid-auto-rows: -webkit-max-content;
    margin: 2em 0;
    padding: 0;
    width: 100%
}

#u-side {
    position: absolute;
    height: 100%;
    width: 100%;

    grid-column: 1/2 !important;
}

.side-container {
    position: -webkit-sticky;
    position: sticky;
    top: 0;
    margin-right: 3em;
    padding-top: 2em;
    font-size: 0.8em;
    color: #888;
padding-left: 2em;
}

.side-border {
    border-right: inset 1px #888;
    padding-right: 1em;
}

.side-container a {
    color: #C44536;
}

#main-content * {
    grid-column: 2/-1;
    max-width: 700px;
}

#main-content #page-content {
    grid-column: 1/-1;
    display: grid;
    grid-column: 1 / -1;
    grid-template-columns: 19em 1fr;
    grid-auto-rows: max-content;
    grid-auto-rows: -webkit-max-content;
    max-width: 100%;
}

#page-content .main {
    grid-column: 2/-1;
    max-width: 700px;
}

#footer {
    margin: 0;
    padding: 0;
    grid-row: 3;
}

#license-area {
    margin: 0;
    padding: 0;
    grid-row: 4;
}

.open-button {
    display: none;
}

#u-side:not(target)~#u-close {
    display: none;
}

@media (max-width: 767px) {
    .open-button {
        display: block;
        position: fixed;
        top: 2em;
        right: 1em;
        z-index: 15;
    }
    :root {
        --header-height: 44px;
    }
    .open-menu a {
        display: none;
    }
    #content-wrap {
        display: block;
    }
    #main-content {
        display: block;
        max-width: 100%;
        -webkit-box-sizing: border-box;
        box-sizing: border-box;
        padding: 0 1em;
    }
    #main-content #page-content {
        display: block;
    }
    #u-side {
        position: fixed;
        top: 0;
        left: 0;
        width: 19em;
        z-index: 2000;
    }
    .side-container {
        padding: 2em;
        font-size: 1em;
        margin-right: 0em;
    }
    .side-border {
        border-right: none;
        padding-right: 0em;
    }
    #u-side:not(target) {
        -webkit-transition: -webkit-transform 300ms 0s ease;
        transition: -webkit-transform 300ms 0s ease;
        -o-transition: transform 300ms 0s ease;
        transition: transform 300ms 0s ease;
        transition: transform 300ms 0s ease, -webkit-transform 300ms 0s ease;
        -webkit-transform: translateX(calc(19em * -1));
        transform: translateX(calc(19em * -1));
    }
    #u-side:target {
        -webkit-box-shadow: 2px 2px 4px;
                box-shadow: 2px 2px 4px;
        background-color: white;
        -webkit-transform: translateX(0);
        transform: translateX(0);
    }
    #u-close {
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        z-index: 1998;
    }
    #u-close a {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        text-indent: -999px;
        z-index: 1999;
    }
    #u-side:target~#u-close {
        display: block;
    }
    #u-side:not(target)~#u-close {
        display: none;
    }
}

h4 {
    padding-top: 1em;
    font-weight: bold;
    font-size: 120%;
    color: #255C99;
}

.contents {
    padding-left: 1em;
}
.imie{
display:none;
}
@media all and (-ms-high-contrast: none) {
.imie{
display:block;
}
.imie  ~ *{
display:none;
}
}
[[/module]]
