[[module CSS]]
h1, #page-title {
    color: #3366cc; /*1级标题、页面标题颜色*/
}
a {
    color: #3366cc; /*链接颜色*/
}
a.newpage {
    color: #3366cc; /*链接新页面颜色*/
}
a:visited {
    color: #4682B4; /*访问过的链接颜色*/
}
a:hover {
    color: #3399ff; /*鼠标悬停链接颜色*/
}
.scp a {
    color: blue;
}
.tale a {
    color: purple;
}
.goi a {
    color: skyblue;
}
.wanderers a {
    color: green;
}
body {
    background-color: #B0C4DE;
}
div#container-wrap {
    background-image: url(none); /*顶栏背景设为无图片*/
    /*background-repeat: repeat-x;*/ /*顶栏背景图片平铺方式：X轴方向平铺*/
}
div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: linear-gradient(to top,
        #3366cc,
        #9966cc 60%);
}
div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: linear-gradient(to top, /*顶栏底栏背景颜色*/
      #0b3851,
      #225c7c);
    /*box-shadow: 0 5px 10px rgba(0,0,0,.3);*/
}
#header {
    /*background-image: url(https://s2.loli.net/2022/09/13/sLP5UuFKI3lJdiQ.webp);*/ /*logo图片*/
    background-repeat: no-repeat; /*logo平铺方式：不平铺*/
}
#search-top-box-input {
    color: #000000; /*搜索栏文字颜色*/
    background-color: #87CEFA; /*搜索栏背景颜色*/
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    color: #000000; /*搜索栏悬停文字颜色*/
    background-color: #87CEEB; /*搜索栏悬停背景颜色*/
}
#search-top-box-form input[type=submit] {
    border-color: #eee; /*搜索按钮边框颜色*/
    color: #95b2de; /*搜索按钮文字颜色*/
    background-color: #478bad; /*搜索按钮背景颜色*/
    background: linear-gradient(to bottom, #225c7c, #15486b, #0b3851); /*搜索按钮渐变背景*/
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-color: #f0fcff; /*搜索按钮悬停边框颜色*/
    color: #f0fcff; /*搜索按钮悬停文字颜色*/
    background-color: #6499b4; /*搜索按钮悬停背景颜色*/
    background: linear-gradient(to bottom, #225c7c, #15486b, #0b3851); /*搜索按钮悬停渐变背景*/
}
#login-status {
    color: #92e5ff; /*账户信息文字颜色*/
}
#login-status a {
    color: #48D1CC; /*账户信息链接颜色*/
}
#login-status ul a {
    color: #3366cc; /*账户信息下拉菜单链接颜色*/
}
#account-topbutton {
    color: #3366cc; /*账户信息下拉菜单文字颜色*/
}

#header h1 a { /*标题*/
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
#header h1 a::before {
    font-family: "Lucida Calligraphy";
    content: "ILLUSION PLANET";
    white-space: nowrap;
    color: #F5F5F5;
    text-shadow: 2px 2px 2px #000000;
    font:
}
#header h2 span { /*副标题*/
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
    user-select: none;
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
}
#header h2 span::before {
    font-family: "黑体";
    content: "幻 象 星";
    white-space: nowrap;
    color: #92e5ff;
    text-shadow: 1px 1px 1px #0c4d66;
}


/* TOP MENU */
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #3366cc; /*顶栏菜单打开时文字颜色*/
}
/* SIDE MENU */
#side-bar a {
    color: #3366cc; /*侧边栏链接颜色*/
}
#side-bar a:visited {
    color: #4682B4; /*侧边栏访问过链接颜色*/
}
#side-bar a:hover {
    color: #3399ff; /*侧边栏链接悬停颜色*/
}
#side-bar .side-block { /*侧边栏方块*/
    border-color: #006666; /*边框颜色*/
    background-color: #edfaff; /*背景颜色*/
    box-shadow: 0 2px 6px rgba(12,77,102,.5); /*阴影*/
}
#side-bar .heading { /*侧边栏标题*/
    color: #336666; /*颜色*/
    border-color: #333333; /*边框颜色，下面那一条*/
}
#side-bar div.menu-item .sub-text {
    color: #336666; /*侧边栏副标题颜色，SCP系列后面的（X000-X999）*/
}

#side-bar .collapsible-block-folded {
    background-image: none; /*侧边栏折叠背景，一个小十字，隐藏*/
}
#side-bar .collapsible-block-unfolded-link {
    border-color: #663366; /*侧边栏折叠打开是边框颜色，下面那一条*/
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #edfaff; /*侧边栏折叠链接颜色*/
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #336666; /*侧边栏折叠链接悬停颜色*/
}
.side-block .menu-item > .image {
    filter: hue-rotate(300deg);
}
/* YUI-TABS */
.yui-navset .yui-content{
    /*background-color: #eeeeee;*/ /*Tab背景颜色*/
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-color: #87CEFA; /*当前Tab标签背景颜色，无图模式下有用*/
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/fisher-yu/colorrr.png); /*当前Tab标签背景图片*/
    color: #000000; /*当前Tab标签文字颜色*/
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background-color: #ADD8E6; /*Tab标签悬停背景颜色，无图模式下有用*/
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/fisher-yu/colorrr.png); /*Tab标签悬停背景图片*/
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #000000; /*Tab标签与内容之间的边框颜色*/
}
@media (max-width: 767px) {
#side-bar {
    background-color: transparent; /*移动端侧边栏背景颜色*/
}
[[/module]]

