content:
[[module css]]
.page-rate-widget-box {
    display: inline-block;
    border-radius: 0px !important;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, .5);
margin-top: -10px;
    margin-bottom: 10px;
    margin-right: 0em;
text-transform:uppercase;
}

.page-rate-widget-box .rate-points {
    background-color: transparent !important;
    border: solid 0px #000;
    border-bottom: solid 2px #000;
    border-right: 0;
    border-radius: 0px;
    color: white;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: transparent !important;
border: 0px;
    border-bottom: solid 1.5px silver;
    font-weight: 900;
    font-size: 1.2em;
    margin-left: 5px;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background-color: transparent;
    color: silver;
    padding: 0 4px;
    margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #6c1616;
    color: white;
    text-decoration: none;
}

.page-rate-widget-box .cancel {
    background-color: transparent;
border: 0px;
    border-bottom: solid 2px #000;
border-radius: 0px;
    font-weight: 900;
    font-size: 1.2em;
    margin-left: 5px;
}

.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #000;
}

.page-rate-widget-box .cancel a:hover {
    background-color: #6c1616;
    color: #fff;
    text-decoration: none;
}



div.toggleCollapse li.unfolded > .colmod-content-anti {
    display: none;
}

div.toggleCollapse li.folded > .colmod-content-anti {
    -webkit-animation: colmod-fade-in 0.2s ease-in-out;
    -moz-animation: colmod-fade-in 0.2s ease-in-out;
    animation: colmod-fade-in 0.2s ease-in-out;
    -webkit-animation-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
    -moz-animation-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
    animation-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
}

div.toggleCollapse li.unfolded > .colmod-content-anti {
    -webkit-animation-name: none;
    -moz-animation-name: none;
    animation-name: none;
}




body,#main-content,#container{background:black;color:white;}
.page-tags,.page-rate-widget-box#search-top-box,#top-bar,#side-bar,#login-status,#page-title,#header,#footer,#license-area,#footer,#license-area{display:none}
a {color: silver;color: silver;}

a:visited {color: silver;color: silver;}


.danke{
padding: 5px 5px 5px 15px;
margin-bottom:10px;
width: 100%;
font-family: monospace;
font-size: 1.1em; }

.agent{
background-color:#002200;
border: 3px solid #55AA55;
color: #77CC77;
}

.site{
background-color:#222200;
border: 3px solid #AAAA55;
color: #DDDD77;
}

div.toggleCollapse li.unfolded > .colmod-content-anti {
    display: none;
}

div.toggleCollapse li.folded > .colmod-content-anti {
    -webkit-animation: colmod-fade-in 0.2s ease-in-out;
    -moz-animation: colmod-fade-in 0.2s ease-in-out;
    animation: colmod-fade-in 0.2s ease-in-out;
    -webkit-animation-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
    -moz-animation-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
    animation-timing-function: cubic-bezier(0.3649610494,0,0.6350389506,1);
}

div.toggleCollapse li.unfolded > .colmod-content-anti {
    -webkit-animation-name: none;
    -moz-animation-name: none;
    animation-name: none;
}

.danke {
    width: unset;
}

@media (min-width: 768px) and (max-width:979px) {
    #main-content {
        margin:0 12em 0 12em;
    }
}

@media (min-width: 980px) {
    #main-content {
        margin: 0 12em 0 12em;
    }
}
