[[html]]
<!-- Sistema di mapping CC usato: Leaflet -->
<!-- https://leafletjs.com/examples/quick-start/ -->
    <head>
        <!-- ▼ ▼ ▼ Non toccate nulla qui! ▼ ▼ ▼ -->
        <!-- Leaflet's CSS -->
        <link rel="stylesheet"
            href="https://unpkg.com/leaflet@1.5.1/dist/leaflet.css"
            integrity="sha512-xwE/Az9zrjBIphAcBb3F6JVqxf46+CDLwfLMHloNu6KEQCAWi6HcDUbeOfBIptF7tcCzusKFjFw2yuvEpDL9wQ=="
            crossorigin=""/>

        <!-- Make sure you put this AFTER Leaflet's CSS -->
        <script src="https://unpkg.com/leaflet@1.5.1/dist/leaflet.js"
            integrity="sha512-GffPMF3RvMeYyc1LWMHtK8EbPv0iNZ8/oTtHPx9/cc2ILxQ+u905qIwdpULaqDkyBKgOaB57QTMg7ztg8Jm2Og=="
            crossorigin=""></script>

        <!-- script per Map Stamen -->
        <script type="text/javascript" src="http://maps.stamen.com/js/tile.stamen.js?v1.3.0"></script>
        <!-- ▲ ▲ ▲ Non toccate nulla qui! ▲ ▲ ▲ -->

    </head>

    <body>
        <!-- ▼ ▼ ▼ Non toccate nulla qui! ▼ ▼ ▼ -->
        <!-- Dimensioni della mappa -->
        <div id="mapid" style="height: 750px; width: 100%;"></div>
        <!-- ▲ ▲ ▲ Non toccate nulla qui! ▲ ▲ ▲ -->

        <script type="text/javascript">
            // Dati su come viene visualizzata la mappa appena viene caricata
            var Latitude = 41.871941
            var Longitude = 12.567380
            var Zoom = 6  // Zoom iniziale della mappa
            var MinZoom = 4 // Valore minimo di zoom
            var MaxZoom = 9 // Valore massimo di zoom
            var Icon_Size = 34  // Dimensione icone sulla mappa
            var Popup_Size = 175 // Dimensione dei popup
            var Image_Size = Popup_Size // Dimensione immagini nei popup (deve essere ≤ Popup_Size)
            var New_Circle_Radius = 18.5 // Circle radius on new articles
            if (Image_Size > Popup_Size)
                { Image_Size = Popup_Size }
            var report_line_color = "orange"
            // File immagine delle diverse icone che appaiono sulla mappa
            var IT_icon = L.icon(Options={
                iconUrl: 'http://fondazionescp.wdfiles.com/local--files/mappa/scp-logo-it-400.png',
                iconRetinaUrl: 'http://fondazionescp.wdfiles.com/local--files/mappa/scp-logo-it-400.png',
                iconSize: Icon_Size
            });
            var EN_icon = L.icon(Options={
                iconUrl: 'http://fondazionescp.wdfiles.com/local--files/mappa/site_en.png',
                iconRetinaUrl: 'http://fondazionescp.wdfiles.com/local--files/mappa/site_en.png',
                iconSize: Icon_Size
            });
            var Safe_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/green_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/green_it.png",
                iconSize: Icon_Size
            });
            var Euclid_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/cyan_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/cyan_it.png",
                iconSize: Icon_Size
            });
            var Keter_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/red_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/red_it.png",
                iconSize: Icon_Size
            });
            var Thaumiel_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/lime_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/lime_it.png",
                iconSize: Icon_Size
            });
            var Apollyon_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/purple_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/purple_it.png",
                iconSize: Icon_Size
            });
            var Neutralized_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/gray_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/gray_it.png",
                iconSize: Icon_Size
            });
            var Report_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/yellow_it.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/yellow_it.png",
                iconSize: Icon_Size
            });
            var CFO_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wikidot.com/local--files/inc:mappa/cfologo_for_map_2.png",
                iconRetinaUrl: "http://fondazionescp.wikidot.com/local--files/inc:mappa/cfologo_for_map_2.png",
                iconSize: Icon_Size
            });
            var RIDIA_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/madao_per_mappa_temp.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/madao_per_mappa_temp.png",
                iconSize: Icon_Size
            });
            var MADAO_icon = L.icon(Options={
                iconUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/madao_per_mappa_temp.png",
                iconRetinaUrl: "http://fondazionescp.wdfiles.com/local--files/mappa/madao_per_mappa_temp.png",
                iconSize: Icon_Size
            });

            // Coordinate dei vari siti
            var LatLong_SitoAsclepio = [46.382650, 11.431260]
            var LatLong_SitoCerere = [42.288441, 13.900870]
            var LatLong_SitoDeus = [41.084167, 16.271389]
            var LatLong_SitoIride = [42.991138, 12.578360]
            var LatLong_SitoMinerva = [40.085899, 8.979820]
            var LatLong_SitoNettunoAppoggio=[42.333333, 10.316667]
            // Eh sì, le unità del Sito Nettuno si spostano randomicamente ^.^
            // Unità 1
            if (Math.random() > 0.5)
            {
                var SN1_min_lat = 41.444657
                var SN1_max_lat = 42.360447
                var SN1_min_long = 16.265986
                var SN1_max_long = 18.176771
            } else {
                var SN1_min_lat = 39.705338
                var SN1_max_lat = 40.180148
                var SN1_min_long = 16.861266
                var SN1_max_long = 17.796288
            };
            var LatLong_SitoNettunoUnita1 =
                [Math.random()*(SN1_max_lat-SN1_min_lat)+SN1_min_lat,
                 Math.random()*(SN1_max_long-SN1_min_long)+SN1_min_long]
            // Unità 2
            var SN2_min_lat = 38.296673
            var SN2_max_lat = 40.662150
            var SN2_min_long = 9.892672
            var SN2_max_long = 13.758169
            var LatLong_SitoNettunoUnita2 =
                [Math.random()*(SN2_max_lat-SN2_min_lat)+SN2_min_lat,
                 Math.random()*(SN2_max_long-SN2_min_long)+SN2_min_long]
            var LatLong_SitoPlutone = [40.860672, 14.843984]
            var LatLong_SitoUrano = [45.262051, 7.920290]
            var LatLong_SitoVesta = [37.599995, 14.015356]
            var LatLong_SitoVittoria = [44.435188, 10.976970]
            var LatLong_SitoVirtus = [41.855131, 12.568386]
            var LatLong_SitoVulcano = [45.657581, 9.963600]


            // ▼ ▼ ▼ Non toccate nulla qui! ▼ ▼ ▼
            var mymap = L.map('mapid', {attributionControl: true})
                         .setView([Latitude, Longitude], Zoom)
            mymap.options.minZoom = MinZoom
            mymap.options.maxZoom = MaxZoom

            // Tile servers: https://wiki.openstreetmap.org/wiki/Tile_servers

            // Sistema di tile CC usato: Maps Stamen
            // Tipi di visualizzazione: "toner", "terrain", "watercolor"
            mymap.addLayer(
                new L.StamenTileLayer(
                    "toner",
                    {detectRetina: true}
                )
            )

            // Per collegare gli SCP ai siti di contenimento
            var siteline = L.polyline([], {color: "lime"}).addTo(mymap);
            //var sitecircle = L.circleMarker([0, 0], {color: "lime", radius: 0}).addTo(mymap);
            function onClick(a)
            {
                siteline.setLatLngs([a.latlng, a.target.latlong_site])
                //sitecircle.setLatLng(a.target.latlong_site).setRadius(New_Circle_Radius)
            }
            function outClick(a)
            {
                siteline.setLatLngs([])
                //sitecircle.setLatLng([]).setRadius(0)
            }



            // Sistema di tile CC usato: Thunderforest
            // https://manage.thunderforest.com/dashboard
            // ▲ qui si trova anche la quota di utilizzo
            // Tipi di visualizzazione: cycle, transport, landscape, outdoors,
            // transport-dark, spinal-map, pioneer, mobile-atlas, neighbourhood
            /*
            L.tileLayer('https://tile.thunderforest.com/cycle/{z}/{x}/{y}.png?apikey=1cf08b91a1834e5eae6442893424c62a',
                {attribution: 'Maps © <a href="http://www.thunderforest.com/">Thunderforest</a>, Data © <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>',
                maxZoom: 18,
                id: 'mapbox.streets',
                accessToken: 'your.mapbox.access.token'
            }).addTo(mymap);
            */


            // Sistema di tile CC usato: Mapbox
            // (!) Per qualche motivo la nostra subscription è expired
            /*
            L.tileLayer('https://api.tiles.mapbox.com/v4/{id}/{z}/{x}/{y}.png?access_token=pk.eyJ1IjoiZm9uZGF6aW9uZXNjcCIsImEiOiJjanZpaDFnZmQwNWdlM3pzMm4yMGYwOW1pIn0.lXtjMasPtttcWJ6Gj34wHA',
                {attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
                maxZoom: 18,
                id: 'mapbox.streets',
                accessToken: 'your.mapbox.access.token'
            }).addTo(mymap);
            */

            // Don't forget to add the / at the end!
            site_link_prefix_url = "https://fondazionescp.wikidot.com/"  // <- attivare questo per la mappina della branca italiana
            //site_link_prefix_url = "http://scp-int.wikidot.com/"  // <- attivare questo per la mappina pubblicata su INT

            // Funzione per creare i marker per gli SCP
            function add_scp(Number, Name, Class, Latitude, Longitude, Site, Image, Description="", New=false)
            {
                var Site_coord
                if (Site.toLowerCase()=="asclepio")
                    { Site_coord = LatLong_SitoAsclepio }
                if (Site.toLowerCase()=="cerere")
                    { Site_coord = LatLong_SitoCerere }
                if (Site.toLowerCase()=="deus")
                    { Site_coord = LatLong_SitoDeus }
                if (Site.toLowerCase()=="iride")
                    { Site_coord = LatLong_SitoIride }
                if (Site.toLowerCase()=="minerva")
                    { Site_coord = LatLong_SitoMinerva }
                if (Site.toLowerCase()=="nettunoappoggio")
                    { Site_coord = LatLong_SitoNettunoAppoggio }
                if (Site.toLowerCase()=="nettunounita1")
                    { Site_coord = LatLong_SitoNettunoUnita1 }
                if (Site.toLowerCase()=="nettunounita2")
                    { Site_coord = LatLong_SitoNettunoUnita2 }
                if (Site.toLowerCase()=="plutone")
                    { Site_coord = LatLong_SitoPlutone }
                if (Site.toLowerCase()=="urano")
                    { Site_coord = LatLong_SitoUrano }
                if (Site.toLowerCase()=="vesta")
                    { Site_coord = LatLong_SitoVesta }
                if (Site.toLowerCase()=="vittoria")
                    { Site_coord = LatLong_SitoVittoria }
                if (Site.toLowerCase()=="virtus")
                    { Site_coord = LatLong_SitoVirtus }
                if (Site.toLowerCase()=="vulcano")
                    { Site_coord = LatLong_SitoVulcano }

                var Class_icon
                if (Class.toLowerCase()=="safe")
                    { Class_icon = Safe_icon }
                if (Class.toLowerCase()=="euclid")
                    { Class_icon = Euclid_icon }
                if (Class.toLowerCase()=="keter")
                    { Class_icon = Keter_icon }
                if (Class.toLowerCase()=="neutralized")
                    { Class_icon = Neutralized_icon }
                if (Class.toLowerCase()=="apollyon")
                    { Class_icon = Apollyon_icon }
                if (Class.toLowerCase()=="thaumiel")
                    { Class_icon = Thaumiel_icon }

                var scp_marker = L.marker(
                    LatLng=[Latitude, Longitude],
                    Options={title: Number + " – “" + Name.replace("<i>", "").replace("</i>", "") + "”",
                             riseOnHover: true,
                             icon: Class_icon})
                        .addTo(mymap).bindPopup(
                            "<a href='"+site_link_prefix_url+Number.toLowerCase()+"' target='_blank'>\
                            <b>"+Number+" – “" + Name + "”</b></a>\
                            <br>" + Description + "<br>\
                            <a href='"+site_link_prefix_url+Number.toLowerCase()+"' target='_blank'>\
                            <img src=" + Image + " width=" + Image_Size + "px></a>",
                        Options={maxWidth: Popup_Size});

                if (Site != "")
                {
                    scp_marker.latlong_site = Site_coord;
                    scp_marker.on('mouseover', onClick).on('mouseout', outClick);
                };

                if (New == true)
                {
                    var circle = L.circleMarker([Latitude, Longitude],
                                          {color: "OrangeRed",
                                           fillColor: "Orange",
                                           fillOpacity: 0.25,
                                           radius: New_Circle_Radius}
                                 ).addTo(mymap);  
                    var tooltip = new L.marker([Latitude, Longitude], {opacity: 0});  //opacity may be set to zero
                    tooltip.bindTooltip("<b>Recente Contenimento:</b><br>" + Number + " — “" + Name + "”",
                                        {permanent: true, offset: [0, 25], opacity: 0.8}).addTo(mymap)
                }
            }


            // Funzione per creare i marker per i Siti
            function add_site(Name, Location, Description, LatitudeLongitude, Link, Image)
            {
                var site_marker = L.marker(
                LatLng=LatitudeLongitude,
                Options={title: Name + " (" + Location + ")",
                         riseOnHover: true,
                         icon: IT_icon})
                    .addTo(mymap).bindPopup(
                        "<a href='" + site_link_prefix_url + Link + "' target='_blank'>\
                        <b>" + Name + " (" + Location + ")</b></a>\
                        <br>" + Description + "<br>\
                        <a href='" + site_link_prefix_url + Link + "' target='_blank'>\
                        <img src=" + Image + " width=" + Image_Size + "px></a>",
                    Options={maxWidth: Popup_Size});
            }


            // Funzione per creare i marker per le tales
            function add_tale(Title, Description, LatitudeLongitude, Link, Image, ConnectedToLatLong=null, New=false)
            {
                var tale_marker = L.marker(
                    LatLng=LatitudeLongitude,
                    Options={title: '“' + Title + '”',
                             riseOnHover: true,
                             icon: Report_icon})
                        .addTo(mymap).bindPopup(
                            "<a href='" + site_link_prefix_url + Link + "' target='_blank'>\
                            <b>“" + Title + "”</b></a>\
                            <br>" + Description + "<br>\
                            <a href='" + site_link_prefix_url + Link + "' target='_blank'>\
                            <img src=" + Image + " width=" + Image_Size + "px></a>",
                        Options={maxWidth: Popup_Size});

                if (ConnectedToLatLong != null)
                {
                    L.polyline([ConnectedToLatLong, LatitudeLongitude],
                           {color: report_line_color}).addTo(mymap);
                }

                if (New == true)
                {
                    var circle = L.circleMarker(LatitudeLongitude,
                                          {color: "OrangeRed",
                                           fillColor: "Orange",
                                           fillOpacity: 0.25,
                                           radius: New_Circle_Radius}
                                 ).addTo(mymap)
                    var tooltip = new L.marker(LatitudeLongitude, {opacity: 0})  //opacity may be set to zero
                    tooltip.bindTooltip("<b>Nuovo Rapporto:</b><br>" + Title,
                                        {permanent: true, offset: [0, 25], opacity: 0.8}).addTo(mymap)
                }


            }


            // Funzione per creare i marker per i GdI (oggetti o tales)
            function add_goi(Name, GoI, LatitudeLongitude, Link, Image, Description="", New=false)
            {
                var GoI_icon
                if (GoI.toLowerCase()=="cfo")
                    { GoI_icon = CFO_icon }
                if (GoI.toLowerCase()=="madao")
                    { GoI_icon = MADAO_icon }
                if (GoI.toLowerCase()=="ridia")
                    { GoI_icon = MADAO_icon }

                // L'HTML dà problemi se nei testi ci sono '<' o '>'
                var goi_marker = L.marker(
                LatLng=LatitudeLongitude,
                Options={title: Name,
                         riseOnHover: true,
                         icon: GoI_icon})
                    .addTo(mymap).bindPopup(
                        "<a href='" + site_link_prefix_url +  Link + "' target='_blank'>\
                        <b>" +  Name.replace("<", "&lt;").replace(">", "&gt;") + "</b></a>\
                        <br>" + Description + "<br>\
                        <a href='" + site_link_prefix_url +  Link + "' target='_blank'>\
                        <img src=" + Image + " width=" + Image_Size + "px></a>",
                    Options={maxWidth: Popup_Size});

                if (New == true)
                {
                    var circle = L.circleMarker(LatitudeLongitude,
                                          {color: "OrangeRed",
                                           fillColor: "Orange",
                                           fillOpacity: 0.25,
                                           radius: New_Circle_Radius}
                                 ).addTo(mymap)
                    var tooltip = new L.marker(LatitudeLongitude, {opacity: 0})  //opacity may be set to zero
                    tooltip.bindTooltip("<b>Nuovo Oggetto " + GoI.toUpperCase() + ":</b><br>" + Name,
                                        {permanent: true, className: Name, offset: [0, 25], opacity: 0.8}).addTo(mymap)
                }
            }

            report_image = "http://fondazionescp.wikidot.com/local--files/inc:mappa/rapporto_per_mappina.png"
            no_image = "http://fondazionescp.wikidot.com/local--files/mappa/nessuna_documentazione.gif"

            // ▲ ▲ ▲ Non toccate nulla qui! ▲ ▲ ▲



            // - - - █ █ █ SITI █ █ █ - - - //

            // I siti sono in ordine alfabetico

            /*
            TEMPLATE:
            (nota: NON cambiare l'ordine di Name, Location, Description, eccetera)

            add_site
            (
                Name = "기지명",
                Location = "",
                Description = "",
                LatitudeLongitude = LatLong_Sito,
                Link = "http://fondazionescp.wikidot.com/siti-italiani#Sito-",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/"
            )
            */

            add_site
            (
                Name = "아스클레피오스 기지",
                Location = "트렌티노 알토아디제주",
                Description = "미생물 변칙존재를 보관하며 의약학과가 소재한다.",
                LatitudeLongitude = LatLong_SitoAsclepio,
                Link = "siti-italiani#Sito-Asclepio",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/asclepio.jpg"
            )

            add_site
            (
                Name = "케레스 기지",
                Location = "아브루초주",
                Description = "동식물 변칙존재를 보관한다.",
                LatitudeLongitude = LatLong_SitoCerere,
                Link = "siti-italiani#Sito-Cerere",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/minerva.JPG"
            )

            add_site
            (
                Name = "데우스 기지",
                Location = "풀리아주 몬테성",
                Description = "시공간 및 여분차원 변칙존재를 보관한다.",
                LatitudeLongitude = LatLong_SitoDeus,
                Link = "siti-italiani#Sito-Deus",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/deus.jpg"
            )

            add_site
            (
                Name = "이리데 기지",
                Location = "움브리아주",
                Description = "인간형 및 지적 변칙존재를 수용한다.",
                LatitudeLongitude = LatLong_SitoIride,
                Link = "siti-italiani#Sito-Iride",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/iride.jpg"
            )

            add_site
            (
                Name = "미네르바 기지",
                Location = "사르데냐주",
                Description = "인식재해성 및 밈적 변칙성을 보관한다.",
                LatitudeLongitude = LatLong_SitoMinerva,
                Link = "siti-italiani#Sito-Minerva",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/deimos.jpeg"
            )

            add_site
            (
                Name = "넵투누스 기지 지원구역",
                Location = "몬테크리스토섬",
                Description = "수생성 및 해양성 변칙존재를 보관한다.",
                LatitudeLongitude = LatLong_SitoNettunoAppoggio,
                Link = "siti-italiani#Sito-Nettuno",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/nettuno-appoggio.jpg"
            )

            add_site
            (
                Name = "넵투누스 기지 제1해양단위",
                Location = "아드리아해 및 이오니아해 해상, 기동성",
                Description = "수생성 및 해양성 변칙존재를 보관한다.",
                LatitudeLongitude = LatLong_SitoNettunoUnita1,
                Link = "siti-italiani#Sito-Nettuno",
                Image = "http://fondazionescp.wdfiles.com/local--files/inc%3Amappa/nettuno1.jpg"
            )

            add_site
            (
                Name = "넵투누스 기지 제2해양단위",
                Location = "티레니아해 해상, 기동성",
                Description = "수생성 및 해양성 변칙존재를 보관한다.",
                LatitudeLongitude = LatLong_SitoNettunoUnita2,
                Link = "siti-italiani#Sito-Nettuno",
                Image = "http://fondazionescp.wdfiles.com/local--files/siti-italiani/nettuno.jpg"
            )

            add_site
            (
                Name = "플루톤 기지",
                Location = "캄파니아주",
                Description = "역사적, 고고학적 변칙존재를 보관한다.",
