[[module CSS]]
:root {
  --header-logo: url(http://scpkosb.wikidot.com/local--files/draft:dr-sagan-41-b2d2/kd) 0px 35px no-repeat;

  --color-bg: repeat-x top/100% url("http://scpkosb.wikidot.com/local--files/draft:dr-sagan-41-b2d2/kd2");
}

.rate-box-with-credit-button, .page-rate-widget-box {
  --color-bg: #404141;
}

#breadcrumbs {
  display: none;
}

 #search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
     background: #FFF;
}

 h2, h3 {
     font-family: 'GowunBatang-Bold', Arial, sans-serif;
}

 #header h1 a::before {
     color: #FFF;
     text-shadow: 0 -3px #404141, 0 3px #404141, 3px 0 #404141, -3px 0 #404141, 2px 2px #404141, 2px -2px #404141, -2px 2px #404141, -2px -2px #404141;
}
.item {
  order: 1;
  border-radius: 5px;
  background: #404141;
  font-size: 110%;
  font-weight: 700;
  text-align: center;
  width: auto;
  margin: 5px;
  padding: 0 15px 0 15px;
}

.loc {
  position: absolute;
  transform: translate(-20px, -7px);
  background: #FFF;
  border: 2px solid #404141;
  padding: 4px;
  border-radius: 15px;
  color: #404141;
  font-size: 80%;
  vertical-align: center;
  text-align: center;
}

ul ul, li ul {
  margin-left: -10px;
}

.footnotes-footer .title {
  display: none;
}

@media (max-width: 676px) {
  :root {
     --color-bg: no-repeat center/auto 100% url("http://scpkosb.wikidot.com/local--files/draft:dr-sagan-41-b2d2/kd2");
  }
}
[[/module]]

[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP&display=swap');
@import url('http://scpko.wdfiles.com/local--files/unfont/UnBatangBold.css');

body {
    font-family: 'Noto Serif JP', 'UnBatangBold', serif;
}

.creditRate, .page-rate-widget-box {
    font-family: verdana, "Nanum Gothic", arial, helvetica, sans-serif !important;
}
