[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Big+Shoulders+Text:wght@100&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Big+Shoulders+Text:wght@200&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Big+Shoulders+Text:wght@400&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Big+Shoulders+Text:wght@700&display=swap');

.main{
    text-decoration: none!important;
}

.main:hover {
    text-decoration: none!important;
}
.main:visited {     
    color: white;
}

.name {
    font-family: "Big Shoulders Text", sans-serif;
    font-size: 3.5em;
    font-weight: 700;
    line-height: 60px;
}

.tale {
    color: #b0d8f7!important;
    font-family: "Big Shoulders Text", sans-serif;
    font-size: 3em;
    font-style: italic;
    font-weight: 700;
}

.subtitle {
    font-family: "Big Shoulders Text", sans-serif;
    color: white;
    FONT-SIZE: 1.4em;
    position: relative;
    font-style: italic;
    font-weight: 200;
    top: -0.7em;
}

@media only screen and (max-width: 26.5625rem) {
.name {
    font-family: "Big Shoulders Text", sans-serif;
    font-size: 2em;
    font-weight: 700;
    line-height: 35px;
}

.tale {
    color: #b0d8f7!important;
    font-family: "Big Shoulders Text", sans-serif;
    font-size: 1em;
    font-style: italic;
    font-weight: 700;
}

.subtitle {
    font-family: "Big Shoulders Text", sans-serif;
    color: white;
    FONT-SIZE: 1em;
    position: relative;
    font-style: italic;
    font-weight: 200;
    top: -0.7em;
}
}

.main {
    display: block;
    height: 18em;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
}

.art {
    position: relative;
    background-position: 25% 40%;
    background-size: 130%;
    background-color: #00295D;
    background-blend-mode: luminosity;
    transition: all 200ms ease-in-out;
    color: white! important;
    margin-left: -20em;
    margin-right: 25em;
    margin-top: 1em;
}

.art:hover {
    color: #b0d8f7!important;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    text-shadow: 1px 1px 0px #051c2e;
    mix-blend-mode: none;
}

.art:hover .subtitle {
    color: white;
}

.art:hover .info {
    color:#557D99!important;
}


.right {
    position: relative;
    background-position: 25% 40%;
    background-size: 140%;
    background-color: #00295D;
    background-blend-mode: luminosity;
    transition: all 200ms ease-in-out;
    color: white! important;
    margin-left: 25em;
    margin-right: -20em;
    margin-top: -19em;
}

.right:hover {
    color: #b0d8f7!important;
    transition: all 150ms ease-in-out;
    background-color: transparent;
    box-shadow: 0px 0px 9px 3px rgba(0,0,0,0.5);
    text-shadow: 1px 1px 0px #051c2e;
    mix-blend-mode: none;
}

.right:hover .subtitle {
    color: white!important;
}

.right:hover .info {
    color:#557D99!important;
}


#page-title{display:none}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(42.2em);
    max-width: 120dvw !important;
    background-image: url(
http://scp-sandbox-3.wikidot.com/local--files/integration-program-art/banner.png);
    background-repeat: no-repeat;
    background-size: contain;
    background-position: top;
    margin-top: -5.5em;
}

@media only screen and (max-width: 26.5625rem) {
   .widebox {
        height: calc(15.2em);
        margin-top: -3.7em;
   }
}


.text {
    position: absolute;
    text-align: center;
    margin-left: 55vmax;
    margin-right: 0.2em;
    margin-top: 1vmax;
    color: white;
    font-family: "Big Shoulders Text", sans-serif;
    max-width: 40rem !important;
    line-height: 1.1;
   text-shadow: 1px 1px 1px black;
}

.one .text {
    position: absolute;
    text-align: center;
    margin-left: 3vmax;
    margin-right: -2vmax;
    margin-top: 29em;
    color: white;
    font-family: "Big Shoulders Text", sans-serif;
    max-width: 40rem !important;
    line-height: 1.1;
}


.text .quote {
   font-size: 2.835em;
   font-weight: 700;
}

.text .sig {
  font-size: 1.89em;
  font-weight: 100;
}

@media only screen and (max-width: 26.5625rem) {
.text {
    position: absolute;
    text-align: center;
    margin-left: 27.5vmax;
    margin-right: 0.7vmax;
    margin-top: -1vmax;
    color: white;
    font-family: "Big Shoulders Text", sans-serif;
    max-width: 40rem !important;
    line-height: 1.1;
   text-shadow: 1px 1px 1px black;
}

.one .text {
    position: absolute;
    text-align: center;
    margin-left: 1vmax;
    margin-right: 30vmax;
    margin-top: 10em;
    color: white;
    font-family: "Big Shoulders Text", sans-serif;
    max-width: 40rem !important;
    line-height: 1.1;
}

.text .quote {
   font-size: 1em;
   font-weight: 700;
}

.text .sig {
  font-size: 0.8em;
  font-weight: 200;
}
}

.about {
display: flex;
flex-flow: column nowrap;
justify-content: center;
align-items: center;
height: 120vh;
max-width: 23.5rem !important;
margin: auto;
font-family: "Big Shoulders Text", sans-serif;
word-spacing: 4px;
color:white;
line-height: 0.96;
}

.about .def{
letter-spacing: 0.1em;
font-size: 420%;
  font-weight: 700;
}

.about .other {
  letter-spacing: 0.1em;
  font-size: 200%;
  font-weight: 200;
}

.about .ooah{
font-size: 155%;
font-weight: 200;
}


@media only screen and (max-width: 26.5625rem) {
.about {
display: flex;
flex-flow: column nowrap;
justify-content: center;
align-items: center;
height: 30vh;
max-width: 3.5rem !important;
margin: auto;
font-family: "Big Shoulders Text", sans-serif;
word-spacing: 4px;
color:white;
line-height: 0.96;
}

.about .def{
letter-spacing: 0.1em;
font-size: 320%;
  font-weight: 700;
}

.about .other {
  letter-spacing: 0.1em;
  font-size: 130%;
  font-weight: 200;
}

.about .main{
font-size: 125.5%;
font-weight: 200;
}
}

.category {
    position: relative;
    font-family: "Big Shoulders Text", sans-serif;
    font-weight: 200;
    word-spacing: 4px;
    color:white;
    font-size: 7em;
    line-height: 0.96;
    margin-left: -2.5em;
    top: 1vmax;
}

@media only screen and (max-width: 26.5625rem) {
.category {
    position: relative;
    font-family: "Big Shoulders Text", sans-serif;
    font-weight: 200;
    word-spacing: 4px;
    color:white;
    font-size: 2.5em;
    line-height: 0.96;
    margin-left: 0.3em;
    top: .5vmax;
}

.main {
    display: block;
    height: 20vmax;
    padding-left: 0.75rem;
    color: white;
    border-radius: 3px;
    margin: -2.5vmin;
}
}


[[/module]]

