[[module css]]
body{
    --barColour: #3648a3;
}


:root{
  --header-title: "SITE-322 AND AREA-179";
  --header-subtitle: "考え方を緩やかに変えていく";
}

@media only screen and (max-width: 26.5625rem) {
    :root {
        --header-title: "INTEGRATION";
    }
    #header::before {
        top: -1rem;
        left: -2rem;
    }
}

a {
    color: #115AAC;
}

a.newpage {
    color: #8dbef4;
}

a:visited {
    color: #0a3666;
}

/* ---- YUI TAB CUSTOMIZATION ----*/

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: #115AAC;
     box-shadow: none;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: #115AAC;
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: #efefef;
     border: unset;
     box-shadow: none;
     box-shadow: none;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: #ffffff;
    /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: #115AAC;
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
     color: #ffffff;
     background-color: #ffffff;
     border-color: transparent;
     box-shadow: none;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;

     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected, /* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background-color: #115AAC;
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: none;
}
 .yui-navset .yui-nav .selected a{
     width: 100%;
     color: #ffffff;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: #ffffff;
     background-color: #115AAC;
}
 .yui-navset .yui-content {
    background-color: #ffffff;
    box-shadow: none;
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
    padding: .5em;
    border: 1px solid #333;
    box-sizing: border-box;
}

div#extra-div-1 {
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A3995762-305-dw2o/logos3.svg);
    filter: opacity(20%);
}

.meta-title {
  border-bottom: solid 1px #bbbbbb;
  color: #990011;
  font-weight: normal;
  margin: 0 0 0.6em;
  padding: 0 0 0.25em;
  font-size: 200%;
}

.meta-title p {
  margin: 0;
}

.body-text {
    margin: 2% 5% 2% 5%;
    padding: 5px 20px 5px 20px;
    background-color: #f2f2f2;
    color: #111;
}

.addendum{
    border-top:solid 2px #115AAC;
    border-bottom:solid 2px #115AAC;
    background:#f1f1e6;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}

.addendum2{
    border-top:solid 2px #115AAC;
    border-bottom:solid 2px #115AAC;
    background:#ffffff;
    width:90%;
    padding:1px;
    margin: 10px auto;
}

.addendum3{
    border-bottom:solid 2px #115AAC;
    background:#ffffff;
    width:90%;
    padding:1px;
    margin: 10px auto;
}

.article{
    border-left:solid 2px #115AAC;
    border-right:solid 2px #115AAC;
    background:#f5f5f5;
    width:90%;
    padding:1px 15px;
    margin: 10px auto;
}
.quote-text {
    display: inline-block;
    border:dashed 1px #999999;
    background:#F5F5F5;
    float:center;
    width:84%;
    padding:1px 15px;
    margin: 10px 10px 10px 30px;
}

@media only screen and (max-width: 600px) {

    .body-text {
        margin: 0% 4% 0% 4%;
    }

    .quote-text {
        margin: 10px 20px 10px 20px;
    }

}

#page-content .info-container {
    --link-color: rgb(var(--swatch-text-general));
}

#page-content .info-container .translation_block {
    display: none;
}

#page-content .info-container .collapsible-block-link,
#page-content .info-container:hover .collapsible-block-link {
    line-height: 1.6em;
}

#page-content .info-container .collapsible-block-folded,
#page-content .info-container .collapsible-block-unfolded-link {
    display: grid;
}

#page-content .info-container .collapsible-block-folded .collapsible-block-link,
#page-content .info-container .collapsible-block-link {
    -webkit-mask: unset;
            mask: unset;
    background: transparent;
    grid-column: 1;
    grid-row: 1;
    opacity: 0;
}

