[[module CSS]]

/* Sexy Containment Procedures CSS Theme by Cyantreuse on base code by InitHello with edits by CyrusFiredawn | CC-by-SA-3.0 */

blockquote{-webkit-margin-start: 0px;-webkit-margin-before: 1em;-webkit-margin-after: 1em;-webkit-margin-end: 0px;border: 1px solid #d60000;border-radius: 10px}
.gray1 blockquote{background-color:#f4f4f4}
.gray2 blockquote{background-color:#e8e8e8}

#header {
    background: url(http://scp-wiki.wdfiles.com/local--files/component%3Asexy-containment-procedures-theme/new_author_page_logo.png) no-repeat left 15px !important;
    background-size: 120px 120px !important;
    max-width: none;
}

#header span {
    color: white;
    margin-left: 10px;
}

#header h2 span {
    display: block;
    margin: 0;
    margin-left: 10px;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: 0px 0px 0px;
}

#header h2 span::before {
    content: "確保、受容、[編集済]";
    color: red;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

#my-account, #account-topbutton {
    color: white !important;
}

#header {
    width: 100%;
    max-width: none;
    right: 0;
    left: 0;

    width: 90%;
    max-width: 980px;
    margin: 0 auto;
}

#top-bar {
    width: 100%;
    max-width: none;
    right: 0;
    left: 0;
    color: black !important;
}

#top-bar a:hover {
    color: black !important;
    background-color: silver !important;
}

div#container-wrap {
    background: url(http://scp-wiki.wdfiles.com/local--files/theme%3Abroken-masquerade/background.png) top left repeat-x !important;
}

h1, #page-title {
    color: black !important;
}

a {
    color: #d60000 !important;
}

body {
    background-color: #fff !important;
    color: #282828 !important;
}

.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    background-color: #444 !important;
    background-image: none !important;
}

.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background-color: #848484 !important;
    background-image: none !important;
    color: #efefef !important;
}

.heading {
    color: #777 !important;
}

.page-rate-widget-box {
    border: 1px solid #d60000;
    box-shadow: 1px 1px 1px #696969;
    border-radius: 3px;
    background-color: black !important;
}

div.page-rate-widget-box .rate-points {
    background-color: black;
    border: none !important;
}

.page-rate-widget-box .rateup {
    background-color: #282828 !important;
    border: none !important;
}
.page-rate-widget-box .rateup a {
    color: white !important;
}

.page-rate-widget-box .ratedown {
    background-color: #282828 !important;
    border: 0  !important;
}
.page-rate-widget-box .ratedown a {
    color: white !important;
}

.page-rate-widget-box .cancel {
    background-color: #282828 !important;
    border: none !important;
}
.page-rate-widget-box .cancel>a {
    color: white !important;
}

/*.yui-content {
    color: #000 !important;
}*/

a.collapsible-block-link {
    color: #d60000 !important;
}

#side-bar a:visited{ color: #910000; }

 a:visited { color: #910000; }

#side-bar .side-block {
    border: 2px solid gray;
    box-shadow: 0 2px 6px #efefef;
    background-color: white !important;
}

#side-bar .heading {
    border-bottom: 2px solid gray;
    color: gray !important;
}

#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: #333 !important;
}

#search-top-box-input {
    background-color: #2a2a2a !important;
    color: white !important;
}

#search-top-box-form input[type="submit"] {
    background: none !important;
    background-color: #2a2a2a !important;
    color: white !important;
}

table.wiki-content-table th {
    color: #000;
}

/* FIX FOR MOBILE GAP BUG */
a {
    word-wrap: break-word;
}
/* Alternative fix
.container-wrap-wrap {
    overflow: hidden;
}*/
/* RATING MODULE */
div.page-rate-widget-box .rate-points {
    background-color: var(--fg-dark);
    border: solid 1px var(--fg-med-dark);
    border-radius: 0px;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: none;
    border-top: solid 1px var(--fg-med-dark);
    border-bottom: solid 1px var(--fg-med-dark);
    font-weight: bold;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: none;
    color: var(--fg-med-dark);
    padding: 0 4px;
    margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: var(--fg-dark);
    color: var(--fg-med-dark);
    text-decoration: none;
}

.page-rate-widget-box .cancel {
    background-color: var(--fg-dark);
    border: solid 1px var(--fg-light);
    border-left: 0;
    border-radius: 0px;
}

.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: var(--fg-light);
}

.page-rate-widget-box .cancel a:hover {
    border-radius: 0px;
    background: var(--fg-dark);
    color: var(--bg);
    text-decoration: none;
}

.scp-image-block {
    border-color: #fce98b;
}

/* off-canvas */

@media (max-width: 767px) {
#side-bar {
    background-color: transparent;
}
}
[[/module]]

