[[include :scp-wiki-cn:theme:foxtrot dark=a|nightfall=a]]

[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=ZCOOL+QingKe+HuangYou&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Abril+Fatface&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Chakra+Petch&display=swap');



:root {

    /* VARIABLES > Core */

    --header-title: "同步失敗";
    --header-subtitle: "我们生活的世界是个幼稚的幻想";
    --logo-img: url(http://scpsandboxcn.wikidot.com/local--files/some-paintings/%E5%B8%9D%E5%9B%BD%E5%BE%BD%E6%A0%87);
    --darkmode-logo-img: url(http://scpsandboxcn.wikidot.com/local--files/some-paintings/%E5%B8%9D%E5%9B%BD%E5%BE%BD%E6%A0%87);
    --logo-opacity:30%

    --head-font: 'Abril Fatface', 'ZCOOL QingKe HuangYou', sans-serif;
    --ui-font: 'Chakra Petch', 'ZCOOL ingKe HuangYou', sans-serif;
    --mono-font: 'JetBrains Mono', 'Fira Code', 幼圆, monospace;
    --page-font: 'Chakra Petch', 'ZCOOL QingKe HuangYou', 'verdana';
    --base-font-size: 0.9rem;
    --page-font-size: 1.3rem;
}

#page-content {
    font-family: 'Chakra Petch', 'zcool qingke huangyou', sans-serif;
    font-weight: 350;
    font-size: var(--page-font-size);
}

#page-title,
.meta-title {
    font-family: var(--page-font), sans-serif;
    font-weight: 400;
    color: #3b3b3b;
    border-bottom: solid 2px rgba(0, 0, 0, 0.2);
    width: fit-content;
    margin: 0 auto 1.5rem;
}


#header h1 a::before,
#header h2::before {
    color: var(--header-txt-color);
    letter-spacing: 1px;
    font-family: 'Abril Fatface', 'ZCOOL QingKe HuangYou', sans-serif !important;
    font-weight: 400;
    text-shadow: none;
}

div#extra-div-1 {
    height: 160px;
    width: 100%;
    top: 7px;
    position: absolute;
    background: var(--logo-img) 10px 30px no-repeat;
    background-size: 130px;
    background-repeat: no-repeat;
    background-position: 50% 50%;
    z-index: -1;
    opacity: 40%;
}

#side-bar .heading {
    color: var(--misc-txt-color);
    border-bottom: solid 2px #cfcfcf;
    font-size: 1rem;
    font-family: var(--head-font);
    font-weight: 400;
    text-transform: uppercase;
}
[[/module]]

[[module css]]
.page {
    display: block;
    overflow: hidden;
    font-family: "Jim Nightshade", "Bradley Hand ITC", sans-serif;
    font-size: 0.89rem;
    font-style: normal;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.page p,
.page ul {
    line-height: 20px;
    margin: 0;
}
[[/module]]

