[[module CSS]]
.scp-image-block.block-right{
    margin: 0 0 1em;
}
@keyframes fade-in {
    0% {opacity: 0;}
    50% {opacity: 0;}
    100% {opacity: 1;}}

.hidden {color: transparent; transition: 1s;}
.hidden:hover {color: inherit; transition: 1.5s;}

/* Credit to James Kanjo for the inline hover tip CSS, found here:
http://community.wikidot.com/howto:inline-hover-tips */

.hover {color: inherit; transition: 1s;}
.hover:hover {color: transparent; transition: 1s;}
.hover span {display: none;}
.hover:hover span {position: absolute; display: inline; background-color: transparent; color: #000; animation: fade-in 1s;}
.hover:hover span span {position: relative;}
[[/module]]
