[[include :scp-wiki:theme:black-highlighter-theme]]
[[include :scp-wiki:component:toggle-sidebar-bhl]]

[[module css]]

@import url('https://scp-wiki.wdfiles.com/local--files/jackalrelated-art/stylesheet-1.css');
@import url('https://scp-wiki.wdfiles.com/local--files/jackalrelated-art/stylesheet-2.css');
@import url('https://scp-wiki.wdfiles.com/local--files/jackalrelated-art/stylesheet-3.css');
@import url('https://scp-wiki.wdfiles.com/local--files/jackalrelated-art/stylesheet-4.css');
@import url('https://fonts.googleapis.com/css2?family=Courier+Prime&display=swap');

:root {
   --theme-base: "black-highlighter";
   --theme-id: "suspirilite";
   --theme-name: "Suspirilite Theme";

   --logo-image: url("https://scp-wiki.wdfiles.com/local--files/jackalrelated-art/suspirilite.png");
   --header-subtitle: "";

   --body-font: "Louis George Cafe", sans-serif;
   --UI-font: "Louis George Cafe", sans-serif;
   --header-font: "Marola", sans-serif;
   --title-font: "Eurofurence", sans-serif;
   --mono-font: "Courier Prime", Courier, monospace;

   --white-monochrome: 255, 0, 0;
   --pale-gray-monochrome: 250, 250, 255;
   --light-gray-monochrome: 82, 0, 0;
   --gray-monochrome: 99, 00, 00;
   --dark-gray-monochrome: 0, 0, 0;
   --black-monochrome: 0, 0, 0;
   --pale-accent: 130, 0, 0;
   --bright-accent: 130, 0, 0;
   --medium-accent: 130, 0, 0;
   --dark-accent: 0, 0, 0;
   --background-black: 0, 0, 0;
   --alternate-white: 255, 255, 255;
   --profondo-rosso: 255, 0, 0;
   --suspiria: 201, 0, 0;
   --blood-red: 166, 0, 0;
   --dried-blood: 130, 0, 0;

   --swatch-menubg-color: var(--background-black);
   --swatch-menutxt-dark-color: var(--profondo-rosso);
   --swatch-menubg-medium-color: var(--background-black);
   --swatch-text-light: var(--profondo-rosso);

   --rating-module-color: var(--profondo-rosso);
   --rating-module-button-color: var(--blood-red);
   --rating-module-text-color: var(--profondo-rosso);

   --swatch-background: var(--background-black);
   --swatch-topmenu-bg-color: var(--background-black);
   --swatch-primary: var(--profondo-rosso);

   --swatch-secondary-color: var(--background-black);
   --swatch-tertiary-color: var(--background-black);
   --swatch-alternate-color: var(--background-black);

   --swatch-text-dark: var(--blood-red);
   --swatch-text-light: var(--profondo-rosso);
   --swatch-important-text: var(--blood-red);
   --swatch-text-secondary-color: var(--suspiria);

   --background-gradient-color: var(--background-black);
   --header-gradient-color-bottom: var(--background-black);
   --header-gradient-color-middle: var(--background-black);
   --header-gradient-color-top: var(--background-black);
   --background-gradient-distance: 0rem;
   --diagonal-stripes: linear-gradient(transparent 0);

   --sidebar-transition-timing: 0.8s ease-in-out 0.1s;

   --hover-link-color: var(--profondo-rosso);
   --newpage-color: var(--suspiria);
   --link-color: var(--suspiria);
	--visited-link-color: var(--dried-blood);

    --ui-button-bg: var(--background-black);
	--ui-button-txt: var(--profondo-rosso);
	--ui-button-hover-bg: var(--blood-red);
	--ui-button-hover-txt: var(--background-black);
	--ui-button-hover-outline: var(--background-black);
}

hr {
   border-top: .0625rem solid rgba(var(--profondo-rosso), 1);
   color: rgb(var(--profondo-rosso));
   background-color: rgb(var(--profondo-rosso));
}

#main-content {
   --tabs-selected-txt: var(--background-black);

   --ui-icon-color: var(--profondo-rosso);
   --ui-icon-bg: var(--background-black);
   --ui-icon-hover-color: var(--background-black);
   --ui-icon-hover-bg: var(--blood-red);

   --pagetags-title-text: var(--profondo-rosso);
   --pagetags-text-color: var(--suspiria);
}

#page-title {
  text-align: center;
  opacity: 0%;
}

#header {
    --login-dropdown-bg-color: var(--background-black);
	--login-dropdown-bg-image: var(--background-black);
	--login-dropdown-border-color: var(--profondo-rosso);
	--login-dropdown-text-color: var(--profondo-rosso);
	--login-dropdown-text-hover-color: var(--background-black);
	--login-dropdown-bg-hover-color: var(--dried-blood);
}

#top-bar{
    --topmenu-category-color: var(--blood-red);
	--topmenu-category-hover-color: var(--profondo-rosso);
	--topmenu-category-hover-bg: 0,0,0,0; /* Transparent */
	--topmenu-hover-border-color: var(--profondo-rosso);

    --dropdown-bg-color: var(--background-black);
	--dropdown-border-color: var(--profondo-rosso);
	--dropdown-links-color: var(--profondo-rosso);
	--dropdown-links-bg-color: 0,0,0,0; 
	--dropdown-links-hover-color: var(--background-black);
	--dropdown-links-hover-bg-color: var(--blood-red);
}

#side-bar, #interwiki{
   font-weight: bold;

   --sidebar-links-hover-text-color: var(--background-black);
   --sideblock-heading-text-color: var(--suspiria);

   --sidebar-collapsible-fld-link-color: var(--profondo-rosso);
   --sidebar-collapsible-ufld-link-color: var(--profondo-rosso);

   --sidebar-collapsible-fld-link-hover-color: var(--background-black);
   --sidebar-collapsible-fld-arrow-hover-color: var(--background-black);

   --sidebar-collapsible-ufld-link-hover-color: var(--background-black);
   --sidebar-collapsible-ufld-arrow-hover-color: var(--background-black);
}

#interwiki .heading {
   color: rgb(var(--blood-red));
}

::-webkit-scrollbar {
  width: 18px;
  background: #transparent;
}

::-webkit-scrollbar-track {
  background: #000000;
}

::-webkit-scrollbar-thumb {
  background: #a60000;
  border: #820000 1px solid;
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: #ff0000;
}

#search-top-box input.empty{
   color: rgb(138, 0, 0);
   background: rgb(0, 0, 0);
}

#header-extra-div-1 {
   --drop-shadow: none;
   filter: drop-shadow(0rem 0rem 0rem var(--drop-shadow));
}

#login-status {
   color: rgb(255,0,0);
   color: rgb(var(--profondo-rosso);
}

#login-status ul a{
   background-color: rgb(var(--background-black);
   color: rgb(var(--profondo-rosso);
}

#footer {
    color: rgb(var(--profondo-rosso,66,66,72));
}
[[/module]]

[[module CSS]]
/*-----------------------------------------------*/
/* Widebox for Large Elements */
.widebox {
   width: 40vw;
   max-width: 40vw !important;
   position: relative;
   left: calc(-20vw + 50%);
}

@media screen and (max-width: 640px) {
  .widebox {
    width: 100vw;
    max-width: 100vw !important;
    left: calc(-50vw + 50%);
  }
}
