[[module CSS]]
/* HEADER */

div#header h1 a:before {
    content: "蛇の手";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "異常は増加しゆくもの";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

/* SIDE BAR */

#side-bar {
    border: 1px solid black;
}

/* TABLE OF CONTENTS */
#toc {
    border: 1px solid black;
    margin-left: 3em;
}

/* LINK RECOLORS */

a {
    color: #059400;
}
a.newpage {
    color: #00a862;
}

#side-bar a:visited{
    color: #059400;
}

a:visited {
    color: #006d2c;
}

/* QUOTE DIVS */

div.serpents-hand{
       border: solid 1px #112211;
       border-left: 5px solid #006C00;
       background-color: #FFF;
       padding-top: 0.5em;
       padding-left: 1em;
       padding-right: 1em;
       padding-bottom: 0.5em;
       margin-top: 0.5em;
       margin-left: 3em;
       margin-right: 3em;
       margin-bottom: 0.5em;
}

div.foundation{
       border: solid 1px #112211;
       border-left: 5px solid #823f3f;
       background-color: #FFF;
       padding-top: 0.5em;
       padding-left: 1em;
       padding-right: 1em;
       padding-bottom: 0.5em;
       margin-top: 0.5em;
       margin-left: 3em;
       margin-right: 3em;
       margin-bottom: 0.5em;
}
div.goc{
       border: solid 1px #112211;
       border-left: 5px solid #5c95e6;
       background-color: #FFF;
       padding-top: 0.5em;
       padding-left: 1em;
       padding-right: 1em;
       padding-bottom: 0.5em;
       margin-top: 0.5em;
       margin-left: 3em;
       margin-right: 3em;
       margin-bottom: 0.5em;
}

blockquote, div.blockquote, #toc {
    background-color: #FFF;
    border: solid 1px #112211;
    padding-top: 0.5em;
    padding-left: 1em;
    padding-right: 1em;
    padding-bottom: 0.5em;
    margin-top: 0.5em;
    margin-left: 3em;
    margin-right: 3em;
    margin-bottom: 0.5em;
}
