[[module CSS]]
/* HEADER */

div#header h1 a:before {
    content: "Serpent's Hand";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "The Anomalous Is On The Rise";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

/* SIDE BAR */

#side-bar {
    border: 1px solid black;
}

/* TABLE OF CONTENTS */
#toc {
    border: 1px solid black;
    margin-left: 3em;
}

/* LINK RECOLORS */

a {
    color: #059400;
}
a.newpage {
    color: #00a862;
}

#side-bar a:visited{
    color: #059400;
}

a:visited {
    color: #006d2c;
}

/* QUOTE DIVS */

div.serpents-hand{
       border: solid 1px #112211;
