/*
박스
*/
.warning {
    position: relative;
    margin: 2em 0;
    padding: 25px 10px 7px;
    border: solid 2px #FFC107;
}
.warning h1{
    position: absolute;
    margin-top: 0;
    display: inline-block;
    top: -2px;
    left: -2px;
    padding: 0 9px;
    height: 25px;
    line-height: 25px;
    vertical-align: middle;
    font-size: 17px;
    background: #FFC107;
    color: #ffffff;
    font-weight: bold;
}
.warning p {
    margin: 0; 
    padding: 0;
}
.danger {
  display: inline-flex;
  justify-content: center;
  align-items: center;
  border-radius: 20%;
  transform: rotate(45deg);
  flex-flow: column wrap; 
  vertical-align: top;
  width: 1em;
  height: 1em;
  border: 2px solid red;
}
.danger p{
  font-weight:bold;
  transform: rotate(-45deg);
}
