[[module CSS]]
:root {
   --color-1: #FFFFFF;
   --color-2: #000000;

   --color-3: #DDDDDD;
   --color-4: #F4F4F4;

   --color-5: #444444;
   --color-6: #7A7A7A;
   --color-7: #969696;
}

/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/jonna-clown-s-director-page/back.png') center top repeat-x;
}

#header {
    z-index: 1;
    background: url('https://scpko.wikidot.com/local--files/jonna-clown-s-director-page/logo.png');
    background-repeat: no-repeat;
    background-position: center;
    background-size: 110px 110px;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 a {
    z-index: 5;
}

#header h1 a::before {
    content: "SCP 재단";
    color: var(--color-2);
    opacity: 0;
    position: absolute;
    width: 100%;
    height: 60%;
    transform: translateX(-50%);
    font-size: 40px;
    text-align: center;
    top: 20%;
    left: 50%;
    z-index: 5;
}

#header h2::after {
    color: #FDFDFD;
    text-align: center;
    position: absolute;
    width: 100%;
    height: 100%;
    transform: translateX(-50%);
    top: 1px;
    left: 50%;
    content: "자까는광대";
}

/* Login Status */
#login-status {
    position: absolute;
    top: 2px;
    right: 0px;
    text-align: right;
    color: var(--color-2);
    z-index: 7;
}

#login-status a {
    color: var(--color-2);
}

#account-options {
    background-color: var(--color-1) !important;
    border: 1px solid var(--color-3) !important;
    color: var(--color-2) !important;
    text-align: left;
}

#account-topbutton {
    border-color: var(--color-2);
}

#login-status ul a {
    color: var(--color-2) !important;
}

/* Search Box */
#search-top-box {
    position: absolute;
    top: 2px;
    left: 0px;
    text-align: left;
    z-index: 7;
}

#search-top-box-form {
    display: inline-flex;
    background: none;
    box-shadow: none;
    border: none;
    border-radius: 0px;
    padding: 0px;
}

#search-top-box-form > input {
    border-radius: 0 !important;
}

#search-top-box-input {
    background-color: var(--color-1) !important;
    color: var(--color-6) !important;
    border: none !important;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: var(--color-4) !important;
    color: var(--color-5) !important;
    border: none !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: var(--color-1);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: none;
    color: var(--color-6);
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: var(--color-1);
    color: var(--color-7);
    border: none;
}

/* Top Bar */
#top-bar {
    display: flex;
    top: 140px;
    z-index: 6;
}

#top-bar a {
    color: var(--color-2);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--color-4);
    border-right: 1px solid var(--color-3);
    border-left: 1px solid var(--color-3);
    color: var(--color-2);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--color-4);
    color: var(--color-2);
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border: none;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border: none;
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}

.c_topbar_arrow {
    color: var(--color-2);
}

/* Page Elements */
#page-title,
.meta-title {
    color: var(--color-5);
    font-size: 2.1em;
    font-weight: bold;
    text-align: center !important;
}

h1, h2, h3, h4, h5, h6 {
    color: var(--color-5);
    font-weight: bold;
}

html {
    overflow-x: hidden;
}

body {
    background-color: var(--color-1);
    color: var(--color-2);
}

a:link {
    color: var(--color-5);
}

a:visited {
    color: var(--color-6);
}

.hovertip {
    background-color: var(--color-1) !important;
    border: none !important;
    box-shadow: 0 1px 3px var(--color-2) !important;
    color: var(--color-2) !important;
}

.scp-image-block {
    background: var(--color-1);
    box-shadow: 1px 2px 3px var(--color-2);
    border: none;
}

.scp-image-block .scp-image-caption {
    background: var(--color-4);
    color: var(--color-2) !important;
    border: none  !important;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: none;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: var(--color-1);
}

::-webkit-scrollbar-thumb {
    background: var(--color-2);
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--color-5);
}

textarea::-webkit-scrollbar-track {
    background: var(--color-4);
}

/* tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    border: 1px solid gainsboro;
    border-bottom: none;
    background: #FCFCFC;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 1px solid var(--color-7);
    color: var(--color-5);
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid var(--color-2);
    color: var(--color-2);
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: var(--color-1);
    border: 1px solid gainsboro;
}

/* Side Bar */
#side-bar {
    background: var(--color-1);
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background: var(--color-1) !important;
    border: none;
    border-radius: 0px;
    box-shadow: 1px 2px 3px var(--color-2);
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 2px var(--color-2);
    color: var(--color-5);
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: var(--color-7);
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: var(--color-5);
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar .collapsible-block-folded a:link {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: var(--color-5);
}

#side-bar a:visited,
#interwiki a:visited {
    color: var(--color-6);
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: var(--color-7);
}

#top-bar .open-menu a {
    background: var(--color-1);
    border-color: var(--color-2);
    border-radius: 0px;
    color: var(--color-2);
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: var(--color-1);
    border: 2px solid var(--color-1);
    border-radius: 0px;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: var(--color-1) !important;
    color: var(--color-2) !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: var(--color-1);
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: var(--color-2);
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: var(--color-5);
    border: var(--color-5);
    color: var(--color-1);
}

.page-rate-widget-box .cancel {
    background: var(--color-1);
}

.page-rate-widget-box .cancel a {
    color: var(--color-2);
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: var(--color-5);
    border: var(--color-5);
    border-radius: 0;
    color: var(--color-1);
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: var(--color-1) !important;
    border: 2px solid var(--color-1) !important;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: 1px var(--color-1) !important;
    color: var(--color-2) !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: var(--color-5);
    border-radius: 0 0 0 0;
    border: none;
    color: var(--color-1) !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: var(--color-1);
    border-color: var(--color-5);
}

.owindow .modal-header {
    background-color: var(--color-1);
}

.owindow .modal-body img {
    background-color: var(--color-1) !important;
    color: var(--color-2);
}

.owindow .title {
    background-color: var(--color-1);
}

.owindow a,
.owindow a:visited {
    color: #var(--color-5);
}

.owindow .active {
    color: var(--color-6);
}

.owindow .content.modal-body img {
    background-color: var(--color-1) !important;
}

.owindow .button-bar a {
    background-color: var(--color-1);
    color: var(--color-2);
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: var(--color-1) !important;
    border-color: var(--color-7);
    color: var(--color-2);
}

table.page-files .highlight {
    background-color: var(--color-1);
}

input.text,
input.text:focus,
input.text:hover {
    background-color: var(--color-1) !important;
    border-color: var(--color-7);
    color: var(--color-2);
}

textarea {
    background-color: var(--color-1);
    color: var(--color-2);
}

.change-textarea-size a {
    background-color: var(--color-1);
    color: var(--color-2);
}

#lock-info {
    background-color: var(--color-1);
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #FCFCFC;
    border-right: 1px solid var(--color-6);
    border-bottom: 1px solid var(--color-6);
    border-top: 1px solid var(--color-3);
    border-left: 1px solid var(--color-3);
    border-radius: 0px;
    padding-top: 5px;
    padding-bottom: 5px;
}

/* media */
@media (max-width: 767px) {
  .yui-navset {
    z-index: 0 !important;
  }
}

/* etc. */
.scp-asdf1-block,
.scp-asdf2-block,
.scp-asdf3-block,
.scp-asdf4-block,
.scp-asdf5-block,
.scp-asdf7-block {
    border: none;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
    background-color: #fff;
    padding-top: 1em;
    padding-right: 15px;
    padding-left: 15px;
    padding-bottom: 1em;
    color: #000;
}

.scp-asdf6-block {
    border: none;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
    background-color: #EDEDED;
    padding-top: 1em;
    padding-right: 15px;
    padding-left: 15px;
    padding-bottom: 1em;
    color: #000;
}

.scp-asdf1-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/bbbb');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}

.scp-asdf2-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/cccc');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}

.scp-asdf3-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/dddd');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}

.scp-asdf4-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/eeee');
    background-position: 50% 50%;
    background-repeat: no-repeat;
