}

.scp-asdf5-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/ffff');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}

.scp-asdf6-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/hhhh');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}

.scp-asdf7-block {
    background-image: url('http://scpko.wikidot.com/local--files/jonna-clown-s-director-page/gggg');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}
