[[module css]]
@import url("http://scp-jp.wdfiles.com/local--code/joicle-catalog-hub");

#header {
  padding-top: 2em;
  height: 130px;
  z-index: 10;
  background: url(http://scp-jp.wdfiles.com/local--files/joicle-catalog-hub/q2.png) 10px 40px no-repeat, url(http://scp-jp.wikidot.com/local--files/k-s-0682/q1-4K.png) calc(100% - 10px) calc(100% - 40px) no-repeat;
}
@media (max-width: 767px) and (min-width:440px) {
  #header {
    background-size: 200px, 180px;
  }
}

@media (max-width: 440px) {
  #header {
    background-size: 130px, 140px;
    height: 100px;
  }
}

/* Standard Image Block */
.scp-image-block {
  float: center;
  clear: right;
  margin: 0 2em 1em 2em;
  border: solid 1px #ff000000;
  box-shadow: 0 1px 6px rgba(0,0,0,.25);
  width: 300px;
}
.scp-image-block.block-left {
  float: left;
  clear: left;
  margin: 0 2em 1em 0;
}
.scp-image-block img {
  border: 0;
  width: 300px;
}
.scp-image-block .scp-image-caption {
  background-color: #ff000000;
  border-top: solid 1px #ff000000;
  padding: 2px 0;
  font-size: 80%;
  font-weight: bold;
  text-align: center;
  width: 300px;
}
.scp-image-block > p {
  margin: 0;
}
.scp-image-block .scp-image-caption > p {
  margin: 0;
  padding: 0 10px;
}
[[/module]]

