    text-indent: 1rem;
    font-family: "White Text", serif;
    font-size: 1.2rem;
}
.chapterstart {
    font-size: 4rem;
    line-height: 8rem;
}
.links {
display: block;
text-align: center;
}
.links a {
    background: rgb(var(--bright-accent));
    color: white;
    padding: 3px;
    border-radius: 4px;
    font-family: monospace;
    white-space: nowrap;
}
.links a:visited {
    color: white;
}
.links a:hover {
    color:rgb(var(--bright-accent));
}
