[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Domine:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=White+Text:ital,wght@0,400;0,600;0,700;1,400;1,600;1,700&display=swap');
#page-title {
    display: none;
}
.splash h1 {
    font-size: 700%;
    font-family: 'Domine', serif;
    font-weight: 700;
    text-align: center;
    margin-bottom: -2rem;
    letter-spacing: 0.25rem;
}
.splash h3 {
    font-size: 300%;
    font-family: 'Domine', serif;
    font-weight: 700;
    text-align: center;
}
.splash {
    font-family: 'Domine', serif;
    font-weight: 400;
    text-align: center;
}
#page-content > p {
    text-justify: inter-word;
    text-align: justify;
    text-indent: 1rem;
    font-family: "White Text", serif;
    font-size: 1.2rem;
}
.chapterstart {
    font-size: 4rem;
    line-height: 8rem;
}
.links {
display: block;
text-align: center;
}
.links a {
    background: rgb(var(--bright-accent));
    color: white;
    padding: 3px;
    border-radius: 4px;
    font-family: monospace;
    white-space: nowrap;
}
.links a:visited {
    color: white;
}
.links a:hover {
    color:rgb(var(--bright-accent));
}
[[/module]]




[[module CSS]]
#page-title {
    display: none !important;
}
@media (min-width: 768px){
    #page-title {
     display: none;
    }
}
