[[include :scp-wiki:theme:basalt hidetitle=a|centertitle=a]]
[[module CSS]]
/* The Most Wrathful Theme ðŸ˜¡ */
/* Based on Wrath Theme by syuzhet */

@import url('https://fonts.googleapis.com/css2?family=Love+Ya+Like+A+Sister&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Teko');
@import url('https://fonts.googleapis.com/css2?family=Oswald');

:root {
    --basalt-primary-color: 255, 255, 255;
    --basalt-secondary-color: 255, 255, 255;
    --basalt-tertiary-color: 255, 255, 255;

    --basalt-overtone: 255, 0, 0;
    --basalt-undertone: 255, 0, 0;
    --basalt-bright-element-color: 255, 0, 0;
    --basalt-dark-element-color: 255, 0, 0;

    --link-color: 255, 0, 0;
    --link-visited-color: 255, 0, 0;
    --link-newpage-color: 255, 0, 0;

    --top-bar-link-color-hover: 255, 255, 255;
    --login-status-text-color-hover: 255, 255, 255;
    --side-bar-link-color-hover: 255, 255, 255;
    --side-bar-link-background-hover: 20, 20, 20;
    --selection-text-color: 255, 255, 255;

    --header-border-width: 0rem;
    --title-size: 1.25rem;
    --subtitle-size: 0rem;
    --side-bar-button-height: 50vh;
    --user-button-width: 2rem;
    --header-logo-size: 67.5%;

    --basalt-UI-dark-palette: 0, 0, 0;
    --top-bar-link-background-hover: 255, 0, 0;

}

#header h1 a::before {
    display: none;
}

#header h1 a span::after {
    margin-bottom: 0;
}

#container #header {
    background: none;
    position: absolute;
    box-shadow: unset;
    backdrop-filter: none;
}

#main-content {
    margin-top: 0;
}

#page-content {
    padding-top: 0;
}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    max-width: 100dvw !important;
}

.widebox .blockquote {
    margin: 4rem auto;
    padding: 2rem 2rem;
}

.widebox .rate-module {
    position: absolute;
    right: 1rem;
    top: 3rem;
    z-index: 9;
}

.headerimage {
    position: relative;
    height: calc(120dvh);
    background-image: url("https://scp-wiki.wikidot.com/local--files/khornecon/banner.svg");
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
}

.headerimage .wrathtext {
    position: relative;
    display: block;
    margin: auto;
    bottom: -75dvh;
    width: 40%;
}

.headerimage .skullseparator {
    position: absolute;
    bottom: -20dvw;
    width: 100%;
    filter: grayscale(100%);
}

.mostWrathfulBox {
    font-family: "Oswald";
    padding-top: 40dvh;
    width: 80%;
    margin: 0 auto;
}

.mostWrathfulBox h1 {
    margin-block: 0.5em;
    font-size: 10em;
    font-family: "Teko";
    font-weight: 900;
    text-shadow: 0.05em 0.05em 0 rgb(255, 0, 0);
    line-height: 1;
}

.mostWrathfulBox h2 {
    font-family: "Oswald";
    font-size: 6em;
    font-weight: 900;
    line-height: 1;
}

.mostWrathfulBox p {
    font-weight: 800;
    font-size: 4em;
    line-height: 1.2;
}

span {
    word-break: keep-all !important;
}

.mostWrathfulBox h1:nth-child(1) {
    margin-top: 1em;
}

.mostWrathfulBox .title h1 {
    margin-bottom: 0em;
}

.mostWrathfulBox .extraTitle h1 {
    font-family: "Love Ya Like A Sister";
}

.mostWrathfulBox .extraTitle h1 span {
    font-size: 80%;
}

.mostWrathfulBox .extraDescription {
    font-size: 1.2em;
}

.mostWrathfulBox .extraDescription p {
    font-size: 100%;
}

.angrySkull {
    float: right;
    shape-outside: circle(50%);
    padding: 5%;
    box-sizing: border-box;
}

@media only screen and (max-width: 1020px) {
    #container #header {
        background: white;
        position: sticky;
    }

    .headerimage {
        height: calc(80dvh);
        overflow-x: clip;
    }

    .headerimage .skullseparator {
        top: calc(55dvh);
        left: -37.5dvw;
        width: 150dvw;
        max-width: 150dvw;
    }

    .headerimage .wrathtext {
        bottom: -40dvh;
        width: 90%;
    }

    .mostWrathfulBox {
        padding-top: 5dvh;
        font-size: 1rem;
    }

    .mostWrathfulBox h1 {
        font-size: 4em;
    }

    .mostWrathfulBox h2 {
        font-size: 3em;
    }

    .mostWrathfulBox p {
        font-weight: 800;
        font-size: 2em;
    }

    .mostWrathfulBox h3 {
        font-size: 2em;
        line-height: 1;
    }

    .mostWrathfulBox .angry {
        width: 100%;
    }

    .mostWrathfulBox .sideskull {
        display: none;
    }  
}
[[/module]]

