[[module CSS]]
/* Layout */
#page-content .grid {
    display: flex;
    overflow: hidden;
}
#page-content .grid-left {
    float: left;
    width: 50%;
}
#page-content .grid-right {
    float: right;
    width: 50%;
}

/* Mobile Media Query */
@media (max-width: 479px) {
#page-content .grid {
    display: block;
}
#page-content .grid-left,
#page-content .grid-right {
    width: 100%;
    float: left;
}
}

/* Note Media Query */
@media (max-width: 580px) {
#page-content .grid {
    display: block;
}
#page-content .grid-left,
#page-content .grid-right {
    width: 100%;
    float: left;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) {
#page-content .grid {
    display: flex;
}
#page-content .grid-left {
    float: left;
    width: 50%;
}
#page-content .grid-right {
    float: right;
    width: 50%;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#page-content .grid {
    display: flex;
}
#page-content .grid-left {
    float: left;
    width: 50%;
}
#page-content .grid-right {
    float: right;
    width: 50%;
}
}
[[/module]]
