[[module css]]

:root {
    --fade-in-delay: 0.05s;
}

/* SLIDE SHOW - BASIC FORMAT FOR VIEWPORT */
.yui-navset .yui-nav li, .yui-navset .yui-navset-top .yui-nav li {
    flex-grow: 0 !important;
}

.yui-navset-top {
    container-type: inline-size;
}

.yui-nav {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
    bottom-border-width: 0px;
}

.yui-nav li {
    display: none !important;
    margin: 0 !important;
}

.yui-nav li.selected {
    display: inline-block !important;
}

.yui-content {
    display: grid;
    padding: 0em !important;
    min-width: 110%;
    margin-left: -5%;
    max-width: 120%;
    border: 2px solid #333 !important;
}

@container (width >= 1em) {
    .yui-content {
        min-height: calc(60cqw * 1.1);
        max-height: calc(60cqw * 1.1);
        margin-top: -73cqw;
    }
    .yui-nav {
        margin-top: 69cqw !important;
}
}

.debugNote {
    margin: auto;
    display: none;
}

@media (max-width: 600px) {
    .debugNote {
        display: block;
}

}

/* Slide Visuals CSS */
.slideContainer {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
    height: 100%;
    width: 100%;
    background-size: cover;
    background-position: center;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/kill-agents-and-you/2560px-Pseudo_Kleinian_OpenCL_214854124_25K.jpg);
    background-blend-mode: saturation;
    background-color: #00000040;
    color:white;
}

.slideContainer p, .slideContainer li {
    font-size: 1.3em;
    line-height: 1.6;
}

