div#header {
    background: url(http://scpsandboxcn.wikidot.com/local--files/hb-azintro/AlzheimerHeadReEn) 10px 10px no-repeat;
    background-position:0em 3em;
    background size:100px 100px;
}
div#container-wrap {
background: url(http://scpsandboxcn.wikidot.com/local--files/original-high/kindom) top left repeat-x;}



body {
    background: rgb(255, 255, 255);
    color: rgb(0, 0, 0);
}
h1 {
    color: rgb(0, 0, 0);
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "天国降临";
    color: #eee;
    text-shadow: 3px 3px 5px rgb(217, 217, 217);
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "回到我们的国";
    font-weight: bold;
    color: rgb(217,217, 217);
    padding: 19px 0;
    white-space: pre;
}



div#side-bar div.side-block {
    border: 1px solid rgb(255, 255, 255);
    box-shadow: 0 2px 6px rgb(217, 217, 217);
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(0, 0, 0);
    border-color: rgb(0, 0, 0);
}
div.open-menu a {
    color: rgb(0, 0, 0)!important;
    border-color: rgb(0, 0, 0)!important;
}
#side-bar a:visited {
    color: rgb(0, 0, 0);
}
#side-bar div.menu-item .sub-text {
    color: rgb(0, 0, 0);
}
#side-bar a,a:visited {
    color: rgb(0, 0, 0);
}
#side-bar img {
    filter: grayscale(1);
    -webkit-filter: grayscale(1);
}
[[/module]]

