[[module css]]
.count-author {
    counter-reset: listAuthor;
}

.count-author > div.listAuthor {
    counter-increment: listAuthor 1;
}

div.listAuthor h1 span.hidden,
div.sumVotes div h1 span.hidden {
    display: none;
}

div.listAuthor h1::before {
    content: counter(listAuthor);
}

div.listAuthor:not(:nth-last-child(1)) h1,
div.sumVotes div:not(:nth-last-child(1)) h1,
div.listAuthor + h1,
div.sumVotes div + h1 {
    display: none;
}

div.sumVotes div h1::after {
    content: counter(sumVotes);
}

div#page-title {
    display: none;
}

@media (min-width: 1024px) {
div.description-container {
    display: grid;
    grid-template-areas: "a b";
    margin-block: 3rem;
}
}

@media (max-width: 1024px) {
div.description-container {
    margin-top: 3rem;
}

div.description-container ~ div.description-container {
    margin-bottom: 3rem;
    margin-top: 0;
}

div.description-container img.image {
    max-width: 200px !important;
    float: right;
}
}
[[/module]]

