[[module css]]
@import url(http://scpsandboxcn.wikidot.com/glacier-theme/code/1);

#header h1 a{
    color: transparent;
    text-decoration: none;
    text-shadow: none;
}

#header h1  a::before {
    content: "恋是恋爱的恋";
    color: #E4EAF4;
    text-shadow: 1px 1px 1px #0B152B;
}

#header h2 span{
     color: transparent;
    text-decoration: none;
    text-shadow: none;
}

#header h2 span::before {
    content: '　愿你初心恒在，依旧朴素谦卑　';
    color: #E4EAF4;
    font-style: normal;
    text-shadow: 1px 1px 1px #0B152B;
}

#page-title {
  display: none;
}

.meta-title {
    border-bottom: solid 1px #bbbbbb;
    color: #2a54ab;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
    font-family: 'Comic Sans MS', 'Microsoft Yahei', san-serif;
    font-size: 200%;
}

.meta-title p {
  margin: 0;
}
