}

.owindow .button-bar {
    float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
    display: none;
}

.open-menu a {
