    <script>
    function displayPoglądy() {
    var spoleczenstwoInputs =
    // Spoleczenstwo
    document.querySelectorAll("input[name^='spoleczenstwo']:checked");
    var spoleczenstwo = 0;
    spoleczenstwoInputs.forEach(input => {
    spoleczenstwo += Number(input.value);
    });

    if (spoleczenstwo >= 8) {
    spoleczenstwoOpcja = "Reactionary";
    } else if (spoleczenstwo >= 4) {
    spoleczenstwoOpcja = "Conservative";
    } else if (spoleczenstwo > -4) {
    spoleczenstwoOpcja = "Moderate";
    } else if (spoleczenstwo > -8) {
    spoleczenstwoOpcja = "Progressive";
    } else {
    spoleczenstwoOpcja = "Liberationist";
    }

    var spoleczenstwoPro = Math.floor(((12 - spoleczenstwo) / 24) * 100);
    var spoleczenstwoReg = 100 - spoleczenstwoPro;

    // Wladza
    var wladzaInputs = document.querySelectorAll("input[name^='wladza']:checked");
    var wladza = 0;
    wladzaInputs.forEach(input => {
    wladza += Number(input.value);
    });

    if (wladza >= 8) {
    wladzaOpcja = "Extremely Centralized";
    } else if (wladza >= 4) {
    wladzaOpcja = "Centralized";
    } else if (wladza > -4) {
    wladzaOpcja = "Balanced";
    } else if (wladza > -8) {
    wladzaOpcja = "Decentralized";
    } else {
    wladzaOpcja = "Extremely Decentralized";
    }

    var wladzaDec = Math.floor(((12 - wladza) / 24) * 100);
    var wladzaCen = 100 - wladzaDec;

    // System
    var systemInputs = document.querySelectorAll("input[name^='system']:checked");
    var system = 0;
    systemInputs.forEach(input => {
    system += Number(input.value);
    });

    if (system >= 8) {
    systemOpcja = "Enhancing the System";
    } else if (system >= 4) {
    systemOpcja = "Working in the System";
    } else if (system > -4) {
    systemOpcja = "Neutral";
    } else if (system > -8) {
    systemOpcja = "Redesigning the System";
    } else {
    systemOpcja = "Creating New System";
    }

    var systemNow = Math.floor(((12 - system) / 24) * 100);
    var systemW = 100 - systemNow;

    // Ekonomia
    var ekonomiaInputs = document.querySelectorAll("input[name^='ekonomia']:checked");
    var ekonomia = 0;
    ekonomiaInputs.forEach(input => {
    ekonomia += Number(input.value);
    });

    if (ekonomia >= 8) {
    ekonomiaOpcja = "Laissez-Faire";
    } else if (ekonomia >= 4) {
    ekonomiaOpcja = "Unregulated";
    } else if (ekonomia > -4) {
    ekonomiaOpcja = "Mixed";
    } else if (ekonomia > -8) {
    ekonomiaOpcja = "Regulated";
    } else {
    ekonomiaOpcja = "Controlled";
    }

    var ekonomiaKon = Math.floor(((12 - ekonomia) / 24) * 100);
    var ekonomiaRyn = 100 - ekonomiaKon;

    // Wyniki
    document.getElementById("resultReg").innerHTML = spoleczenstwoReg + "%";
    document.getElementById("resultPro").innerHTML = spoleczenstwoPro + "%";
    document.getElementById("resultSpol").innerHTML = spoleczenstwoOpcja;
    document.getElementById("reg").style.width = spoleczenstwoReg + "%";
    document.getElementById("pro").style.width = spoleczenstwoPro + "%";

    document.getElementById("resultCen").innerHTML = wladzaCen + "%";
    document.getElementById("resultDec").innerHTML = wladzaDec + "%";
    document.getElementById("resultWladza").innerHTML = wladzaOpcja;
    document.getElementById("cen").style.width = wladzaCen + "%";
    document.getElementById("dec").style.width = wladzaDec + "%";

    document.getElementById("resultW").innerHTML = systemW + "%";
    document.getElementById("resultNow").innerHTML = systemNow + "%";
    document.getElementById("resultSystem").innerHTML = systemOpcja;
    document.getElementById("w").style.width = systemW + "%";
    document.getElementById("now").style.width = systemNow + "%";

    document.getElementById("resultRyn").innerHTML = ekonomiaRyn + "%";
    document.getElementById("resultKon").innerHTML = ekonomiaKon + "%";
    document.getElementById("resultEkonomia").innerHTML = ekonomiaOpcja;
    document.getElementById("ryn").style.width = ekonomiaRyn + "%";
    document.getElementById("kon").style.width = ekonomiaKon + "%";

	document.getElementById("wyniki").style.display = "inline-block";

    var mcd = 0
    var cent = 0
    var ci = 0
    var pent = 0
    var gocreg = 0
    var sh = 0
    var van = 0
    var gocpro = 0

    if (ekonomia >= 8) {
        mcd = 1;
    } else if (spoleczenstwo <= -4) {
        if (system >= 0) {
            if (wladza >= 0) {
                gocpro = 1;
            } else {
                van = 1;
            }
        } else {
            if (wladza <= 0) {
                sh = 1;
            } else {
                van = 1;
            }
        }
    } else if (spoleczenstwo >= 4) {
        if (system >= 0) {
            if (wladza >= 0) {
                gocreg = 1;
            } else {
                pent = 1;
            }
        } else {
            if (wladza <= 0) {
                ci = 1;
            } else {
                pent = 1;
            }
        }
    } else if (ekonomia >= 4) {
        mcd = 1;
    } else {
        cent = 1;
    }

    // gocreg = 1

    if (mcd == 1) {
    document.getElementById("goi").innerHTML = "Marshall, Carter & Dark";
    document.getElementById("opis1").innerHTML = "Marshall, Carter & Dark is a private megacorporation specializing in anomalous goods. Its supporters believe that deregulation, lack of government interference, and the free operation of companies, such as MC&D will lead to the most effective improvement of the quality of life through economic and technological development.";
    document.getElementById("opis2").innerHTML = "The company has a neutral, though slightly sceptical, opinion on social changes, considering them something trivial and unworthy of attention, focusing on, in their opinion, more important economic issues.";
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/mcd.png";
    document.getElementById("ikonka").style.backgroundColor = "#640364";
    }
    if (cent == 1) {
    document.getElementById("goi").innerHTML = "Centrism";
    document.getElementById("opis1").innerHTML = "Centrism is a view that does not have strong opinions on social issues. While the exact opinions of individual centrists vary widely, most prefer a stable world in which all changes — if any at all — are slow and very gradual. Centrists believe that both sides of the political debate have their points, which should be tolerated and listened to, but the truth lies somewhere in the middle.";
    document.getElementById("opis2").innerHTML = "Centrists oppose the radical conservatism of the Chaos Insurgency and the revolutionary thought of the Serpent's Hand. They hold a neutral opinion towards organizations like the Global Occult Coalition or Vanguard, usually sympathizing with them to a certain degree due to the fact that they are currently the dominant forces. According to centrists, every extreme is by definition bad, so we should keep to common sense, weighing our actions carefully and not falling for radical ideas.";
    }
    if (ci == 1) {
    document.getElementById("goi").innerHTML = "Chaos Insurgency";
    document.getElementById("opis1").innerHTML = "The Chaos Insurgency is a collective term for a range of loosely affiliated radical conservative and regressive groups that believe in the separation of the baseline and anomalous parts of society. Supporters of the Insurgency often believe that humans possess many qualities that prove their superiority over other species; this view is often combined with strong bigotry and sympathies for fascist beliefs.";
    document.getElementById("opis2").innerHTML = "The Chaos Insurgency stands in opposition to the Serpent's Hand and Vanguard -- their actions in favor of anomalies and progressivism are the exact opposite of the Insurgency's ideology, which sympathizes with more militaristic groups, like the Global Occult Coalition and PENTAGRAM, although they often come into conflict with them due to the Insurgency's drastic revolutionary methods of operation.";
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/ci.png";
    document.getElementById("ikonka").style.padding = "0px";
    document.getElementById("ikonka").style.width = "140px";
    document.getElementById("ikonka").style.backgroundColor = "";
    }
    if (pent == 1) {
    document.getElementById("goi").innerHTML = "PENTAGRAM";
    document.getElementById("opis1").innerHTML = "PENTAGRAM is the occult branch of the U.S. Department of Defence. It supports a strong and stable national government and is skeptical towards progressive social changes and the organizations that support them. While the group shares social views with the regressive arm of the Global Occult Coalition, <a href='https://scp-wiki.wikidot.com/art:public-release-of-operation-witness-materials' target='_blank'>even engaging in joint actions with them</a>, they disagree on whether national or international power should hold more influence.";
    document.getElementById("opis2").innerHTML = 'Many other national agencies that used to be in charge of maintaining normalcy — like the French National Gendastrerie or the Polish ZM "Witch Slayer" Division — also share similar views to PENTAGRAM.';
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/penta.png";
    document.getElementById("ikonka").style.padding = "0px";
    document.getElementById("ikonka").style.width = "140px";
    document.getElementById("ikonka").style.backgroundColor = "";
    }
    if (gocreg == 1) {
    document.getElementById("goi").innerHTML = "The Regressive Faction of the Global Occult Coalition";
    document.getElementById("opis1").innerHTML = "The Regressive Faction of the Global Occult Coalition believes in a strong centralized world government, ruled by a firm hand. The main representatives of this faction within Council of 108 are the Bavarian Illuminati, the Holy Order of the Knights Templar, Reformed, and the United Church of Satan, Scientist.";
    document.getElementById("opis2").innerHTML = "Conservative members of the Coalition rarely cooperate with other organizations — with a few <a href='https://scp-wiki.wikidot.com/art:public-release-of-operation-witness-materials' target='_blank'>exceptions in the form of selected former normalcy organizations that share their social views</a> — believing that the Council of 108 and its members should represent the highest global authority.";
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/goc.png";
    document.getElementById("ikonka").style.backgroundColor = "#212850";
    }
    if (sh == 1) {
    document.getElementById("goi").innerHTML = "Serpent's Hand";
    document.getElementById("opis1").innerHTML = "The Serpent's Hand is a collective term for a range of loosely affiliated groups that are united by the idea of fighting for the rights of parahumans and paranormal beings. Members of many of the groups that make up the Hand are individuals with extremely progressive views; they believe in overthrowing unjust hierarchies through direct action and often sympathize with far-left views.";
    document.getElementById("opis2").innerHTML = "In the modern world, the Hand's activities mainly focus on left-wing activism, which involves concern for minorities — especially paranormal ones — and resisting the hegemony of organizations such as the Global Occult Coalition.";
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/sh.png";
    document.getElementById("ikonka").style.backgroundColor = "#0b5105";
    }
    if (van == 1) {
    document.getElementById("goi").innerHTML = "Vanguard";
    document.getElementById("opis1").innerHTML = "Vanguard is an organization formed from the leftovers of the former SCP Foundation and some of the Serpent's Hand groups. Its main goal is to change the status quo through normalizing the existence of anomalous phenomena, objects, and people and taking action to educate and protect the public from the possible dangers of the aforementioned phenomena.";
    document.getElementById("opis2").innerHTML = "Vanguard takes pride in its peaceful modus operandi, which focuses on cooperation with other organizations. Although it agrees with groups such as the Serpent's Hand on many issues, Vanguard is more in favor of reforms within the system than direct action, although it sometimes agrees that those are needed as well. Because of its approach and opinions, the organization often comes into conflict with Marshall, Carter & Dark, PENTAGRAM, and the Regressive Faction of the Global Occult Coalition.";
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/van.png";
    document.getElementById("ikonka").style.backgroundColor = "#80b874";
    }
    if (gocpro == 1) {
    document.getElementById("goi").innerHTML = "The Progressive Faction of the Global Occult Coalition";
    document.getElementById("opis1").innerHTML = "The Progressive Faction of the Global Occult Coalition believes in a strong centralized world government that represents the interests of every member of the paranormal world. The main representatives of this faction within the Council of 108 are the Ancient Noble Order of the Gormogons, the International Center for the Study of Unified Thaumatology, and the Sidhe Lounge.";
    document.getElementById("opis2").innerHTML = "Progressive members of the Coalition often cooperate with Vanguard, although they show more resentment toward the Serpent's Hand critiquing their modus operandi and believing that the only way to make changes is through internal reforms of the system.";
    document.getElementById("ikonka").src = "https://scp-wiki.wdfiles.com/local--files/kompas-parapolityczny-na-trudne-czasy/goc.png";
    document.getElementById("ikonka").style.backgroundColor = "#212850";
    }
