}

.resultWrapper > div.resultFirst {
  flex: 0 0 100%;
}

.resultWrapper > div.resultOthers {
  flex: 1 0 30%;
}

.resultWrapper > div.resultNonSCP {
  flex: 1 0 45%;
}

.resultWrapper > div > span {
