[[module CSS]]

a,a:visited {
    color: rgb(204, 0, 0);
}

@media (max-width: 767px) {
#side-bar {
    background-color: #6E6E6E;
}
}
body {

    background: rgb(0, 0, 0);

}

h1 {

    color: rgb(255, 255, 255);

}

div#container-wrap {

    background: url(https://scpsandboxcn.wikidot.com/local--files/lanperouge/1.png) top left repeat-x;

}

div#header {

    background: url(https://scpsandboxcn.wikidot.com/local--files/lanperouge/%E6%9C%AA%E6%A0%87%E9%A2%98-1.png) 10px 40px no-repeat;

    background-size: 6rem



}

div#header h1 a span {

    font-size: 0px;
}
div#header h1 a:before {
    content: "咕噜";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "咕噜，咕噜，咕噜。";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

#page-title {
    color: rgb(255, 255, 255);
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(0, 0, 0);
    color: rgb(255, 255, 255);
}

div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: rgb(0, 0, 0)!important;
    border: solid rgb(0, 0, 0) 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid rgb(0, 0, 0)!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: rgb(0, 0, 0)!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

div#side-bar div.side-block {
    background: #FFFFFF;
    border: #C0C0C0;
    box-shadow: 0  6px #808080;
}

div.open-menu a {
    color: rgb(204, 0, 0)!important;
    border-color: rgb(204, 0, 0)!important;
}

[[/module]]

