[[module CSS]]

/*

參考改編自流浪者圖書館的經典主題頁
圖片素材由[[user Dr V Valentine]]繪製
  */

h1, #page-title {
    color: #dd388e;
}

#page-title {
    border-color: #bbb;
}

/* LINK RECOLORS鏈接回放器 */

a {
    color: #006c84;
}
a.newpage {
    color: #009aa8;
}

#side-bar a:visited{ 
    color: #006c84;
}

a:visited {
    color: #006c84;
}

/* HEADER IMAGES標題 */
div#container-wrap {
    background-image: url(https://i.imgur.com/tODPQY3.png);
}

#header {
     background-image: url(http://scp-jp.wikidot.com/local--files/laundry-of-miya/xiyi-mini.png);
}

/* SEARCH BAR RECOLOR搜索欄重新調整 */

#search-top-box-input {
    border: solid 1px #999;
    color: #ccc;
    background-color: #001a33;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: #333e66;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
    border: solid 1px #999;
    color: #ccc;
    background-color: #336666;
    background: linear-gradient(to bottom, #667399, #333966, #000b33);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25) ;
    background-color: #668099;
    background: linear-gradient(to bottom, #000b33, #667399, #333966);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

/* LOGIN STATUS登錄狀態*/
#login-status ul a {
    color: #006c84;
}

/* HEADER TEXT標題文本*/
#header h1 a {
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi, 'Nanum Gothic', Arial, sans-serif;
    text-decoration: none;
    text-shadow: 3px 3px 5px transparent;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "洗濯部門";
    color: #001a33;
}

#header h2 span {
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: 1px 1px 1px transparent;
}

#header h2 span::before {
    content: "洗浄・修繕・保養";
    color:#71c7d5;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* SIDE BAR邊欄*/
#side-bar {
    padding: 10px;
    border: 2px dotted black;
    background-color: white !important;
    box-shadow: 0 2px 6px rgba(0,30,0,.5);
    border-radius: 10px;
    margin: auto;
}

#side-bar .side-block{
    border: none;
    box-shadow: none; 
    background-color: white !important;
    margin-bottom: 0px;
    padding: 3px;
}

#side-bar .heading {
    color: #001a33;
    border-bottom: solid 1px #001a33;
    width: 80%;
    margin: auto;
    margin-top: 1em;
}

#side-bar .collapsible-block-folded {
    background-image: url(https://i.imgur.com/h7uO8BI.png);
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #001a33;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #001166;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #006c84;
}

.side-block .menu-item > .image {
    filter: hue-rotate(140deg) brightness(150%);
    -webkit-filter: hue-rotate(140deg) brightness(150%);
}

/* TOP-BAR */
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #006c84;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #006c84;
}

/* SOME NICE BOXES */
div.sexy-box {
    background: #eee;
    border: 1px solid #ccc;
}
div.sexy-box div.image-container img {
    border: 1px solid #999;
}

/* CONTENT PANELS */
.content-panel {
    border: solid 1px #888880;
    border-radius: 10px;
    background-color: #999990;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
}
.content-panel.standalone {
    background: #fcfdfb;
}
.content-panel .panel-heading {
    color: #ffffff;
    text-shadow: 1px 1px 2px rgba(0,0,0,.35);
}
.content-panel .panel-body {
    background: #f0f6ff url(http://www.zeros-pg.wikidot.com/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
}
.content-panel .panel-footer {
    color: #fffff0;
    text-shadow: 1px 1px 2px rgba(0,0,0,.5);
}
.content-panel .panel-footer a {
    color: #ffffff;
}
.content-panel .content-toc {
    background-color: #fff;
    border: solid 1px #ccc;
    box-shadow: inset 1px 2px 6px rgba(0,0,0,.15)
}

/*自定義頁面評級模塊 */
div.page-rate-widget-box .rate-points {
  background-color: #6ab82d;
  border: solid 1px #6ab82d;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #82e038;
  border-top: solid 1px #6ab82d;
  border-bottom: solid 1px #6ab82d;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #6ab82d;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #6ab82d;
  color: #82e038;
}
.page-rate-widget-box .cancel {
  background-color: #6ab82d;
  border: solid 1px #6ab82d;
}
.page-rate-widget-box .cancel a {
  color: #82e038;
}
.page-rate-widget-box .cancel a:hover {
  background: #82e038;#
  color: #82e038;
}

/* INFO MODULE COMPATIBILITY */
.rate-box-with-credit-button {
    background-color: #6ab82d !important;
    border-color: #6ab82d !important;
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}
.creditButton p a {
    border-left-color: #ecefff !important;
}

/* AYER'S INFO MODULE */
:root {
--barColour: #6ab82d;
}

/* Standard Image Block */
.scp-image-block {
    border: solid 1px #666;
    box-shadow: 0 1px 6px rgba(0,0,0,.25);
}
.scp-image-block .scp-image-caption {
    background-color: #eee;
    border-top: solid 1px #666;
}

/* MOBILE SIDEBAR */

@media (max-width: 767px) {   
#side-bar {
    background-color: white;
}

#side-bar:target {
    border: 2px dotted black;
}

#side-bar:target .close-menu { background: none; }
}

/* footnote by 7happy7 */
span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:before,
sup.footnoteref > a.footnoteref:before {
content: "(";
}
span:not([class]):not([style]) > sup.footnoteref > a.footnoteref:before {
content: ",";
}

/* TRANSLATION BLOCK */
iframe.scpnet-interwiki-frame {
  filter: hue-rotate(140deg) brightness(150%);
  -webkit-filter: hue-rotate(140deg) brightness(150%);
}

/* 分頁籤 */
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background:#f4a018;
    color: rgb(0, 0, 0);
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background:#fff98c;
    color: rgb(0, 0, 0);
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background:#f4d318;
    color: rgb(0, 0, 0);
}

[[/module]]

