 .alwrap {
    position: fixed;
    top: 15%;
    left: 10%;
    max-height: 80%;
    width: 80%;
    z-index: 999999;
}
@keyframes alwrap {
    from {opacity: 0;pointer-events: none;}
    to {opacity: 1;pointer-events: auto;}
}
@keyframes alcon {
    from {transform: scale(0);}
    to {transform: none;}
}

.premier a { color:gold }
.deuxiÃ¨me a { color:silver }
.troisiÃ¨me a { color:goldenrod }

.alwrap .unfolded {
    animation: alwrap 0.25s 0.5s both;
    background: #191970;
    box-shadow: 1px 1px 5px #aaa;
    display: flex;
    flex-direction: column-reverse;
    padding: 1em;
    text-align: center;
    z-index: 10;
}

.alwrap .unfolded .colmod-content {
    text-align: center;
}

.alwrap .colmod-content {
    padding: 2em;
}
.alwrap .colmod-content .h {
    animation: alcon 0.25s 0.55s both;
}
.alwrap .colmod-content .c {
    animation: alcon 0.25s 0.65s both;
    margin-bottom: 1em;
    max-height: 50vh;
    overflow-y: scroll;
    scrollbar-width: none;  
    -ms-overflow-style: none;
    -webkit-font-smoothing: antialiased;
}
.alwrap .colmod-content .c::-webkit-scrollbar {
    display:none;
}

.alwrap .foldable-list-container a {
    display: inline-block;
}
.alwrap .foldable-list-container a:last-child {
    background: #eee;
    border: 1px solid #bbb;
    border-radius: 2px;
    color: #555;
    font-weight: bold;
    padding: 0.3em 0.75em;
    transition: all 0.25s ease-in;
}
.alwrap .foldable-list-container a:last-child:hover {
    background: #ddd;
    border: 1px solid #69d;
    outline: none;
    text-decoration: none;
}
.alwrap .foldable-list-container a:last-child:before {
    content: "";
    /*background-color: rgba(0,0,0,.2);*/
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: -1;
}

.alwrap .foldable-list-container a:first-child {
    background: rgba(255,255,255,.5);
    border-radius: 50%;
    bottom: .5rem;
    box-shadow: 0 0 0.25rem #999;
    color: rgba(200,200,200,.8);
    display: flex;
    font-size: 0;
    height: 3rem;
    position: fixed;
    text-shadow: 0 0 .15rem;
    transition: all .25s ease;
    right: .5rem;
    width: 3rem;
}
.alwrap .foldable-list-container a:first-child:hover {
    box-shadow: 0 0 0.5rem #999;
    text-decoration: none;
    text-shadow: 0 0 .25rem;
}
.alwrap .foldable-list-container a:first-child:before {
    align-items: center;
    content: "\f129";
    display: flex;
    font: normal normal normal 14px/1 FontAwesome;
    font-size: 1.6rem;
    justify-content: center;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    position: relative;
    transform: translate(0, 0);
    width: 100%;
}
